// frontend/src/App.js

import React, { useState, useEffect } from 'react';
import ArrayBar from './components/ArrayBar'; 

// Array data the backend uses to generate steps
const INITIAL_ARRAY = [10, 4, 15, 2, 7, 18, 1, 9]; 
const ANIMATION_SPEED_MS = 100; // Time delay between each sorting step

function App() {
  const [steps, setSteps] = useState(null);
  const [currentStepIndex, setCurrentStepIndex] = useState(0);
  const [selectedAlgorithm, setSelectedAlgorithm] = useState("bubblesort"); // State for algorithm
  const [loadingError, setLoadingError] = useState(null);

  // --- Derived State for Rendering ---
  const currentStep = steps ? steps[currentStepIndex] : null;
  const currentArray = currentStep ? currentStep.array : INITIAL_ARRAY;
  const highlightIndices = currentStep ? currentStep.highlight_indices : [];
  const isSortingComplete = steps && currentStepIndex >= steps.length - 1;

  // --- 1. Fetch Data on Load (Run when selectedAlgorithm changes) ---
  useEffect(() => {
    const testData = {
      algorithm: selectedAlgorithm, // Use the state variable here
      array: INITIAL_ARRAY 
    };

    // Reset state before fetching
    setSteps(null);
    setCurrentStepIndex(0);
    setLoadingError(null);

    // Fetch data using the proxy route
    fetch('/api/visualize', { 
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(testData),
    })
    .then(response => {
        if (!response.ok) {
            throw new Error(`HTTP error! status: ${response.status}`);
        }
        return response.json();
    })
    .then(data => {
      console.log(`SUCCESS! Data received from Flask for ${selectedAlgorithm}:`, data);
      setSteps(data);
    })
    .catch(error => {
        console.error('API Communication Failed:', error);
        setLoadingError(`Failed to fetch data: ${error.message}. Check Flask server logs.`);
    });
  }, [selectedAlgorithm]); // Rerun whenever the selected algorithm changes

  // --- 2. Animation Timer Logic ---
  useEffect(() => {
    if (!steps || currentStepIndex >= steps.length - 1) return;

    const intervalId = setInterval(() => {
      setCurrentStepIndex(prevIndex => prevIndex + 1);
    }, ANIMATION_SPEED_MS); 

    return () => clearInterval(intervalId); // Cleanup function

  }, [steps, currentStepIndex]);
  // --- END Animation Logic ---

  return (
    <div style={{ padding: '20px' }}>
      <h1>Sorting Visualizer</h1>

      {/* ALGORITHM SELECTOR UI */}
      <div style={{ marginBottom: '20px' }}>
          <label htmlFor="algo-select" style={{ marginRight: '10px', fontWeight: 'bold' }}>Choose Algorithm:</label>
          <select 
              id="algo-select" 
              value={selectedAlgorithm} 
              onChange={(e) => {
                  setSelectedAlgorithm(e.target.value);
                  // The useEffect hook above handles the actual data fetching/reset
              }}>
              <option value="bubblesort">Bubble Sort (O(nÂ²))</option>
              <option value="mergesort">Merge Sort (O(n log n))</option>
          </select>
      </div>

      {loadingError && <p style={{color: 'red'}}>Error: {loadingError}</p>}

      {/* Container for the bars */}
      <div 
        style={{ 
          display: 'flex', 
          justifyContent: 'center', 
          alignItems: 'flex-end', 
          height: '400px',
          width: '100%',
          borderBottom: '2px solid #ccc'
        }}>

        {currentArray.map((value, index) => (
          <ArrayBar 
            key={index}
            value={value}
            // Highlight bars that were marked for comparison/swap in the current step
            isHighlighted={highlightIndices.includes(index)}
          />
        ))}
      </div>

      {/* Status Bar */}
      <div style={{ marginTop: '20px' }}>
          {!steps && <p>Loading steps...</p>}
          {steps && isSortingComplete && 
              <h2 style={{color: '#4CAF50'}}>Sorting Complete! (Total Steps: {steps.length})</h2>}
          {steps && !isSortingComplete && currentStep &&
              <p>Step {currentStepIndex + 1} of {steps.length}. Action: **{currentStep.action}**</p>}
      </div>
    </div>
  );
}

export default App;
