import React, { useState, useEffect, useCallback } from 'react';

// --- Components (Moved locally for single-file mandate) ---

// 1. ArrayBar Component
const ArrayBar = React.memo(({ value, height, width, isHighlighted, isSorted }) => {
    // Green: Sorted, Orange/Red: Highlighted (Comparison/Swap), Blue: Default
    const color = isSorted ? '#4CAF50' : isHighlighted ? '#FF5733' : '#007bff';
    const finalWidth = width > 0 ? `${width}px` : 'auto';

    return (
        <div 
            style={{
                height: `${height}px`,
                width: finalWidth,
                margin: '0 1px',
                backgroundColor: color,
                transition: 'height 0.1s linear, background-color 0.1s linear',
                borderRadius: '2px 2px 0 0',
                display: 'flex',
                alignItems: 'flex-end',
                justifyContent: 'center',
                fontSize: '10px',
                color: 'white',
                fontWeight: 'bold',
                position: 'relative',
            }}
            title={`Value: ${value}`}
        >
            {/* Show value for smaller arrays */}
            {width > 20 && value}
        </div>
    );
});

// 2. BST Visualizer Component (Simplified/Dummy)
const BSTVisualizer = () => {
    const [treeData, setTreeData] = useState([]);
    const [inputValue, setInputValue] = useState('');
    const [message, setMessage] = useState('Enter a number to build the BST.');

    const insertNode = useCallback((data, value) => {
        if (!data) return { value: value, left: null, right: null };
        if (value < data.value) {
            data.left = insertNode(data.left, value);
        } else if (value > data.value) {
            data.right = insertNode(data.right, value);
        }
        return data;
    }, []);

    const handleInsert = () => {
        const num = parseInt(inputValue);
        if (isNaN(num)) {
            setMessage('Please enter a valid number.');
            return;
        }

        let newTreeData = [...treeData];
        if (newTreeData.length === 0) {
            newTreeData.push({ value: num, left: null, right: null });
        } else {
            // Only update the root element (which is the single object in the array)
            newTreeData = [insertNode(newTreeData[0], num)];
        }

        setTreeData(newTreeData);
        setInputValue('');
        setMessage(`Inserted ${num}. Try inserting another number.`);
    };

    // A simple function to render the tree (just showing the root for simplicity)
    const renderTree = (node) => {
        if (!node) return null;
        return (
            <div style={{ margin: '10px', padding: '10px', border: '1px solid #ccc', borderRadius: '8px', display: 'inline-block', backgroundColor: '#e9f7ff' }}>
                <p style={{ fontWeight: 'bold', margin: 0 }}>Root: {node.value}</p>
                <div style={{ display: 'flex', justifyContent: 'center', marginTop: '10px' }}>
                    {node.left && <div style={{ marginRight: '10px', borderRight: '1px dashed #ccc' }}>L: {renderTree(node.left)}</div>}
                    {node.right && <div style={{ marginLeft: '10px', borderLeft: '1px dashed #ccc' }}>R: {renderTree(node.right)}</div>}
                </div>
            </div>
        );
    };

    return (
        <div>
            <h1>Binary Search Tree Visualizer</h1>
            <p className="text-sm text-gray-600">{message}</p>
            <div style={{ display: 'flex', gap: '10px', marginBottom: '20px' }}>
                <input
                    type="number"
                    value={inputValue}
                    onChange={(e) => setInputValue(e.target.value)}
                    placeholder="Enter number"
                    style={{ padding: '8px', border: '1px solid #ccc', borderRadius: '4px', minWidth: '150px' }}
                />
                <button 
                    onClick={handleInsert} 
                    style={{ padding: '8px 15px', cursor: 'pointer', backgroundColor: '#007bff', color: 'white', border: 'none', borderRadius: '4px' }}
                >
                    Insert Node
                </button>
            </div>

            <div style={{ 
                minHeight: '400px', 
                backgroundColor: '#f9f9f9', 
                padding: '20px', 
                border: '1px solid #eee',
                borderRadius: '8px',
                textAlign: 'center'
            }}>
                {treeData.length > 0 ? (
                    renderTree(treeData[0])
                ) : (
                    <p style={{marginTop: '150px', color: '#888'}}>Tree is empty. Insert the first node!</p>
                )}
            </div>
        </div>
    );
};


// --- Constants ---
const DEFAULT_ARRAY_SIZE = 15;
const MAX_ARRAY_VALUE = 400; 
const MIN_ARRAY_VALUE = 10;
const ANIMATION_SPEED_MS = 80; 

// --- Algorithm Pseudocode ---
const PSEUDOCODE = {
    bubblesort: [
        "function bubbleSort(array):",
        "  n = array.length",
        "  for i from 0 to n - 2:",
        "    for j from 0 to n - 2 - i:",
        "      // Compare & Highlight array[j] and array[j+1]",
        "      if array[j] > array[j+1]:",
        "        // Swap elements",
        "        swap(array[j], array[j+1])",
        "  return array"
    ],
    mergesort: [
        "function mergeSort(array):",
        "  if array.length <= 1: return array",
        "  mid = floor(array.length / 2)",
        "  left = mergeSort(array[0...mid])",
        "  right = mergeSort(array[mid...n])",
        "  return merge(left, right)",
        "",
        "function merge(left, right):",
        "  // Merging sub-arrays and placing back into the original array...",
        "  result = []",
        "  while left & right are not empty:",
        "    if left[0] <= right[0]:",
        "      result.push(left.shift())",
        "    else:",
        "      result.push(right.shift())",
        "  return result + left + right"
    ]
};

// --- Helper Functions ---
const generateRandomArray = (size) => {
    return Array.from({ length: size }, () => 
        Math.floor(Math.random() * (MAX_ARRAY_VALUE - MIN_ARRAY_VALUE + 1)) + MIN_ARRAY_VALUE
    );
};

const parseCustomArray = (inputString) => {
    const elements = inputString.split(',')
        .map(s => {
            const num = parseInt(s.trim());
            return isNaN(num) || num <= 0 ? null : Math.min(num, MAX_ARRAY_VALUE);
        })
        .filter(n => n !== null);
    return elements;
};

// --- CLIENT-SIDE SORTING LOGIC (The fix for the Bubble Sort issue) ---

/**
 * Generates steps for Bubble Sort visualization.
 */
const getBubbleSortSteps = (initialArray) => {
    const array = [...initialArray];
    const steps = [];
    const n = array.length;
    let sortedIndices = [];

    for (let i = 0; i < n - 1; i++) {
        let swapped = false;
        for (let j = 0; j < n - 1 - i; j++) {
            // 1. Highlight comparison
            steps.push({
                array: [...array], 
                highlight_indices: [j, j + 1], 
                action: `Comparing array[${j}] and array[${j + 1}]`,
                sorted_indices: [...sortedIndices]
            });

            if (array[j] > array[j + 1]) {
                // 2. Swap
                [array[j], array[j + 1]] = [array[j + 1], array[j]];
                swapped = true;
                steps.push({
                    array: [...array], 
                    highlight_indices: [j, j + 1], 
                    action: `Swapping ${array[j+1]} and ${array[j]}`, 
                    sorted_indices: [...sortedIndices]
                });
            }
        }

        // Mark the largest element of this pass as sorted
        if (n - 1 - i >= 0) {
            sortedIndices.push(n - 1 - i);
        }

        // If no two elements were swapped in the inner loop, the array is sorted
        if (!swapped) {
            // Mark remaining elements as sorted and break
            for (let k = 0; k < n - 1 - i; k++) {
                if (!sortedIndices.includes(k)) {
                     sortedIndices.push(k);
                }
            }
            break;
        }
    }

    // Final step: Mark all as sorted
    const finalSortedIndices = Array.from({ length: n }, (_, i) => i);
    steps.push({
        array: [...array], 
        highlight_indices: [], 
        action: "Finished Bubble Sort",
        sorted_indices: finalSortedIndices
    });

    return steps;
};

/**
 * Generates steps for Merge Sort visualization.
 */
const getMergeSortSteps = (initialArray) => {
    const array = [...initialArray];
    const steps = [];

    // Recursive Merge function that records steps
    const merge = (arr, start, mid, end) => {
        const left = arr.slice(start, mid);
        const right = arr.slice(mid, end);
        let i = 0, j = 0, k = start;

        // Step 1: Highlight the sub-array being merged
        steps.push({
            array: [...array],
            highlight_indices: Array.from({ length: end - start }, (_, idx) => start + idx),
            action: `Merging sub-arrays from index ${start} to ${end - 1}`,
            sorted_indices: []
        });

        while (i < left.length && j < right.length) {
            // Step 2: Comparison and placement
            if (left[i] <= right[j]) {
                array[k] = left[i];
                i++;
            } else {
                array[k] = right[j];
                j++;
            }
            k++;

            // Record step after each placement
            steps.push({
                array: [...array],
                highlight_indices: [k - 1], // Highlight the placed bar
                action: `Placing element at index ${k - 1}`,
                sorted_indices: []
            });
        }

        // Step 3: Handle remaining elements
        while (i < left.length) {
            array[k] = left[i];
            i++; k++;
            steps.push({
                array: [...array],
                highlight_indices: [k - 1],
                action: `Placing remaining element from left half at ${k - 1}`,
                sorted_indices: []
            });
        }
        while (j < right.length) {
            array[k] = right[j];
            j++; k++;
            steps.push({
                array: [...array],
                highlight_indices: [k - 1],
                action: `Placing remaining element from right half at ${k - 1}`,
                sorted_indices: []
            });
        }
    };

    const mergeSortHelper = (arr, start, end) => {
        if (end - start <= 1) return;

        const mid = Math.floor((start + end) / 2);
        mergeSortHelper(arr, start, mid);
        mergeSortHelper(arr, mid, end);
        merge(arr, start, mid, end);
    };

    mergeSortHelper(array, 0, array.length);

    // Final step: Mark all as sorted
    const finalSortedIndices = Array.from({ length: array.length }, (_, i) => i);
    steps.push({
        array: [...array], 
        highlight_indices: [], 
        action: "Finished Merge Sort",
        sorted_indices: finalSortedIndices
    });

    return steps;
};

function App() {
  // NEW STATE: Controls the current view ('sort' or 'bst')
  const [currentView, setCurrentView] = useState('sort'); 

  // Sorting Visualizer States
  const [steps, setSteps] = useState(null);
  const [currentStepIndex, setCurrentStepIndex] = useState(0);
  const [selectedAlgorithm, setSelectedAlgorithm] = useState("bubblesort"); 
  const [loadingError, setLoadingError] = useState(null);
  const [inputArray, setInputArray] = useState(() => generateRandomArray(DEFAULT_ARRAY_SIZE));
  const [arraySize, setArraySize] = useState(DEFAULT_ARRAY_SIZE);
  const [customInputData, setCustomInputData] = useState("");
  const [speed, setSpeed] = useState(ANIMATION_SPEED_MS);

  // Derived States
  const currentStep = steps ? steps[currentStepIndex] : null;
  const currentArray = currentStep ? currentStep.array : inputArray;
  const highlightIndices = currentStep ? currentStep.highlight_indices : [];
  const sortedIndices = currentStep ? currentStep.sorted_indices || [] : [];
  const isSortingComplete = steps && currentStepIndex >= steps.length - 1 && sortedIndices.length === inputArray.length;
  const currentPseudocode = PSEUDOCODE[selectedAlgorithm];
  const isSupportedAlgorithm = ["bubblesort", "mergesort"].includes(selectedAlgorithm);


  // --- Sorting Visualizer Helper Functions ---
  const handleGenerateNewArray = (size = arraySize) => {
      const newArray = generateRandomArray(size);
      setInputArray(newArray);
      setCustomInputData(""); 
      setArraySize(size);
  };

  const handleLoadCustomArray = () => {
      const newArray = parseCustomArray(customInputData);
      if (newArray.length > 0) {
          setInputArray(newArray);
          setArraySize(newArray.length); 
          setLoadingError(null);
      } else {
          setLoadingError("Invalid or empty custom array input. Please use comma-separated integers (e.g., 5,12,3,40).");
      }
  };


  // --- 1. Generate Data on Array/Algorithm Change (Sorting only) ---
  // This replaces the API fetch with local function calls.
  useEffect(() => {
    if (currentView !== 'sort' || inputArray.length === 0) return;

    // Clear steps/error before generation
    setSteps(null);
    setCurrentStepIndex(0);
    setLoadingError(null);

    if (!isSupportedAlgorithm) {
        return; 
    }

    const cleanedArray = inputArray.map(val => Number(val));
    let newSteps;

    try {
        if (selectedAlgorithm === "bubblesort") {
            newSteps = getBubbleSortSteps(cleanedArray);
        } else if (selectedAlgorithm === "mergesort") {
            newSteps = getMergeSortSteps(cleanedArray);
        }

        if (newSteps && newSteps.length > 0) {
            setSteps(newSteps);
        } else {
             setLoadingError(`Algorithm ${selectedAlgorithm} failed to generate steps or resulted in an empty step list.`);
        }

    } catch (error) {
        console.error('Local Algorithm Generation Failed:', error);
        setLoadingError(`Local generation failed for ${selectedAlgorithm}: ${error.message}`);
    }

  }, [selectedAlgorithm, inputArray, isSupportedAlgorithm, currentView]); 

  // --- 2. Animation Timer Logic (Sorting only) ---
  useEffect(() => {
    // CONDITION 1: Check if we are in the sort view
    if (currentView !== 'sort') return;

    // CONDITION 2: Check if steps data is loaded and sorting is not yet complete
    if (!steps || currentStepIndex >= steps.length - 1) return;

    const intervalId = setInterval(() => {
      setCurrentStepIndex(prevIndex => prevIndex + 1);
    }, speed); // Use dynamic speed state

    return () => clearInterval(intervalId); 

  }, [steps, currentStepIndex, currentView, speed]); // Animation relies on steps, index, view, and speed

  // --- Algorithm Options Structure ---
  const algorithmOptions = [
    { label: "--- Supported Algorithms ---", value: "header-supported", disabled: true },
    { label: "Bubble Sort (O(n²)) - RUNNING", value: "bubblesort" },
    { label: "Merge Sort (O(n log n)) - RUNNING", value: "mergesort" },
    { label: "--- Other Sorting Algorithms ---", value: "header-sort", disabled: true },
    { label: "Selection Sort (O(n²))", value: "selectionsort" },
    { label: "Insertion Sort (O(n²))", value: "insertionsort" },
    { label: "Quick Sort (O(n log n))", value: "quicksort" },
    { label: "Heap Sort (O(n log n))", value: "heapsort" },
    { label: "--- Search Algorithms ---", value: "header-search", disabled: true },
    { label: "Linear Search (O(n))", value: "linearsearch" },
    { label: "Binary Search (O(log n))", value: "binarysearch" },
  ];


  return (
    <div style={{ padding: '20px', minWidth: '800px', fontFamily: 'Inter, sans-serif' }}>

      {/* VIEW TOGGLE BUTTONS */}
      <div style={{ marginBottom: '20px', borderBottom: '2px solid #eee', paddingBottom: '10px' }}>
        <button 
          onClick={() => setCurrentView('sort')} 
          style={{ 
            padding: '10px 20px', 
            marginRight: '10px', 
            fontWeight: currentView === 'sort' ? 'bold' : 'normal',
            backgroundColor: currentView === 'sort' ? '#007bff' : '#f0f0f0',
            color: currentView === 'sort' ? 'white' : '#333',
            border: 'none',
            borderRadius: '5px',
            cursor: 'pointer',
            boxShadow: currentView === 'sort' ? '0 2px 4px rgba(0,0,0,0.2)' : 'none'
          }}>
          Array Visualizer (Sorting & Search) 📈
        </button>
        <button 
          onClick={() => setCurrentView('bst')} 
          style={{ 
            padding: '10px 20px', 
            fontWeight: currentView === 'bst' ? 'bold' : 'normal',
            backgroundColor: currentView === 'bst' ? '#007bff' : '#f0f0f0',
            color: currentView === 'bst' ? 'white' : '#333',
            border: 'none',
            borderRadius: '5px',
            cursor: 'pointer',
            boxShadow: currentView === 'bst' ? '0 2px 4px rgba(0,0,0,0.2)' : 'none'
          }}>
          Binary Search Tree Visualizer 🌳
        </button>
      </div>

      {currentView === 'sort' ? (
        // === ARRAY VISUALIZER (SORTING & SEARCH) ===
        <>
          <h1 style={{fontSize: '24px', marginBottom: '15px'}}>Array Sorting & Search Visualizer</h1>

          {/* CONTROL PANEL */}
          <div style={{ marginBottom: '20px', padding: '15px', borderRadius: '8px', backgroundColor: '#f4f7fa', display: 'flex', flexWrap: 'wrap', gap: '20px', alignItems: 'center', boxShadow: '0 4px 6px -1px rgba(0, 0, 0, 0.1)' }}>

              {/* Algorithm Selector */}
              <div>
                  <label htmlFor="algo-select" style={{ marginRight: '10px', fontWeight: 'bold' }}>Algorithm:</label>
                  <select 
                      id="algo-select" 
                      value={selectedAlgorithm} 
                      onChange={(e) => setSelectedAlgorithm(e.target.value)}
                      style={{ padding: '8px', border: '1px solid #ccc', borderRadius: '4px' }}>
                      {algorithmOptions.map(option => (
                          <option 
                              key={option.value} 
                              value={option.value} 
                              disabled={option.disabled}>
                              {option.label}
                          </option>
                      ))}
                  </select>
              </div>

              {/* New Random Array Button */}
              <button 
                onClick={() => handleGenerateNewArray()} 
                style={{ 
                    padding: '8px 15px', 
                    cursor: 'pointer', 
                    border: 'none', 
                    borderRadius: '4px', 
                    backgroundColor: '#FFC107', 
                    color: '#333', 
                    fontWeight: 'bold' 
                }}>
                  Generate New Array
              </button>

              {/* Custom Array Input */}
              <div style={{ display: 'flex', gap: '10px', alignItems: 'center' }}>
                <input
                    type="text"
                    placeholder="Custom Array (e.g., 5,12,3,40)"
                    value={customInputData}
                    onChange={(e) => setCustomInputData(e.target.value)}
                    style={{ padding: '8px', minWidth: '200px', border: '1px solid #ccc', borderRadius: '4px' }}
                />
                <button 
                    onClick={handleLoadCustomArray} 
                    style={{ 
                        padding: '8px 15px', 
                        cursor: 'pointer', 
                        backgroundColor: '#28A745', 
                        color: 'white', 
                        border: 'none', 
                        borderRadius: '4px',
                        fontWeight: 'bold'
                    }}>
                    Load Custom Array
                </button>
              </div>

              {/* Speed Slider */}
              <div style={{ marginLeft: 'auto' }}>
                  <label htmlFor="speed-slider" style={{ marginRight: '10px', fontWeight: 'bold' }}>Speed ({(1000/speed).toFixed(1)} ops/sec):</label>
                  <input 
                      id="speed-slider"
                      type="range"
                      min="10" 
                      max="500" 
                      value={speed}
                      onChange={(e) => setSpeed(Number(e.target.value))} 
                  />
              </div>
          </div>

          {loadingError && <p style={{color: '#D9534F', backgroundColor: '#FEE', padding: '10px', borderRadius: '4px'}}>**Error:** {loadingError}</p>}

          {/* Visualization Container */}
          <div 
            style={{ 
              display: 'flex', 
              justifyContent: 'center', 
              alignItems: 'flex-end', 
              height: '400px',
              width: '100%',
              borderBottom: '3px solid #333',
              backgroundColor: '#ffffff',
              paddingTop: '20px'
            }}>

            {isSupportedAlgorithm ? currentArray.map((value, index) => (
              <ArrayBar 
                key={index}
                value={value}
                height={(value / MAX_ARRAY_VALUE) * 380} 
                // Ensure width scales based on array size
                width={Math.max(5, (800 / arraySize) - 2)} 
                isHighlighted={highlightIndices.includes(index)}
                isSorted={isSortingComplete || sortedIndices.includes(index)}
              />
            )) : <h3 style={{marginTop: '150px', color: '#888'}}>Visualization not available for **{selectedAlgorithm.toUpperCase().replace(/SEARCH|SORT/g, ' ')}**. Please select Bubble Sort or Merge Sort.</h3>}
          </div>

          {/* Status Bar & Pseudocode Area */}
          <div style={{ marginTop: '20px', display: 'flex', gap: '40px' }}>

              {/* Status Bar */}
              <div style={{ flex: 1 }}>
                  <h2 style={{fontSize: '20px', borderBottom: '1px solid #ccc', paddingBottom: '5px'}}>Algorithm Status</h2>
                  {isSupportedAlgorithm ? (
                      <>
                          {!steps && <p>Generating steps...</p>}
                          {steps && isSortingComplete && 
                              <h3 style={{color: '#28A745', fontSize: '18px'}}>Sorting Complete! Array is sorted.</h3>}
                          {steps && !isSortingComplete && currentStep &&
                              <p style={{fontSize: '16px'}}>**Step {currentStepIndex + 1}** of {steps.length}. Action: *{currentStep.action}*</p>}
                          {steps && !isSortingComplete && !currentStep &&
                              <p>Starting sort...</p>}
                      </>
                  ) : (
                      <p style={{color: '#FFC107', fontWeight: 'bold'}}>Algorithm implementation coming soon! Select a supported algorithm to visualize.</p>
                  )}
              </div>

              {/* Pseudocode Area */}
              <div style={{ flex: 1, borderLeft: '1px solid #ccc', paddingLeft: '20px' }}>
                  <h2 style={{fontSize: '20px', borderBottom: '1px solid #ccc', paddingBottom: '5px'}}>Pseudocode: {selectedAlgorithm}</h2>
                  <div style={{ backgroundColor: '#f9f9f9', padding: '10px', borderRadius: '5px', fontSize: '14px', fontFamily: 'monospace', whiteSpace: 'pre-wrap', maxHeight: '200px', overflowY: 'auto', border: '1px solid #eee' }}>
                    {currentPseudocode ? currentPseudocode.map((line, index) => (
                        <div 
                            key={index} 
                            style={{ 
                                // Simple line highlighting is not implemented in the new steps object yet, but the structure is here
                                // backgroundColor: currentStep?.line === index ? '#ffe0b2' : 'transparent', 
                                transition: 'background-color 0.2s',
                                padding: '1px 0'
                            }}>
                            {line}
                        </div>
                    )) : <p>// Pseudocode not yet available for this algorithm.</p>}
                  </div>
              </div>

          </div>
        </>
      ) : (
        // === BST VISUALIZER ===
        <BSTVisualizer />
      )}

      {/* DEVELOPER CREDIT LINE (Always visible) */}
      <div style={{ marginTop: '50px', paddingTop: '10px', borderTop: '1px solid #eee', textAlign: 'center', fontSize: '12px', color: '#666' }}>
          Developed by: **Shubham Jana** | Contact: <a href="mailto:devshubhamjana@gmail.com">**devshubhamjana@gmail.com**</a>
      </div>

    </div>
  );
}

export default App;
