// frontend/src/App.js

import React, { useState, useEffect } from 'react';
import ArrayBar from './components/ArrayBar'; 

// --- Constants ---
const DEFAULT_ARRAY_SIZE = 10;
const MAX_ARRAY_VALUE = 400; 
const MIN_ARRAY_VALUE = 10;
const ANIMATION_SPEED_MS = 100;

// --- Algorithm Definitions ---
const PSEUDOCODE = {
    bubblesort: [
        "function bubbleSort(array):",
        "  n = array.length",
        "  for i from 0 to n - 2:",
        "    for j from 0 to n - 2 - i:",
        "      if array[j] > array[j+1]:",
        "        swap(array[j], array[j+1])",
        "  return array"
    ],
    mergesort: [
        "function mergeSort(array):",
        "  if array.length <= 1: return array",
        "  mid = floor(array.length / 2)",
        "  left = mergeSort(array[0...mid])",
        "  right = mergeSort(array[mid...n])",
        "  return merge(left, right)",
        "",
        "function merge(left, right):",
        "  result = []",
        "  while left & right are not empty:",
        "    if left[0] <= right[0]:",
        "      result.push(left.shift())",
        "    else:",
        "      result.push(right.shift())",
        "  return result + left + right"
    ]
    // Other pseudocode can be added here (e.g., selectionsort, quicksort)
};

// --- Helper Functions ---
const generateRandomArray = (size) => {
    return Array.from({ length: size }, () => 
        Math.floor(Math.random() * (MAX_ARRAY_VALUE - MIN_ARRAY_VALUE + 1)) + MIN_ARRAY_VALUE
    );
};

const parseCustomArray = (inputString) => {
    const elements = inputString.split(',')
        .map(s => {
            const num = parseInt(s.trim());
            return isNaN(num) || num <= 0 ? null : Math.min(num, MAX_ARRAY_VALUE);
        })
        .filter(n => n !== null);
    return elements;
};


function App() {
  const [steps, setSteps] = useState(null);
  const [currentStepIndex, setCurrentStepIndex] = useState(0);
  // Default to a working algorithm
  const [selectedAlgorithm, setSelectedAlgorithm] = useState("bubblesort"); 
  const [loadingError, setLoadingError] = useState(null);
  const [inputArray, setInputArray] = useState(() => generateRandomArray(DEFAULT_ARRAY_SIZE));
  const [arraySize, setArraySize] = useState(DEFAULT_ARRAY_SIZE);
  const [customInputData, setCustomInputData] = useState("");

  // --- Derived State for Rendering ---
  const currentStep = steps ? steps[currentStepIndex] : null;
  const currentArray = currentStep ? currentStep.array : inputArray;
  const highlightIndices = currentStep ? currentStep.highlight_indices : [];
  const isSortingComplete = steps && currentStepIndex >= steps.length - 1;
  const currentPseudocode = PSEUDOCODE[selectedAlgorithm];

  // Check if the selected algorithm is supported by the backend/visualization
  const isSupportedAlgorithm = ["bubblesort", "mergesort"].includes(selectedAlgorithm);


  // Function to create a new random array and update state
  const handleGenerateNewArray = (size = arraySize) => {
      const newArray = generateRandomArray(size);
      setInputArray(newArray);
      setCustomInputData(""); 
      setArraySize(size);
  };

  // Function to load the custom array from the text input
  const handleLoadCustomArray = () => {
      const newArray = parseCustomArray(customInputData);
      if (newArray.length > 0) {
          setInputArray(newArray);
          setArraySize(newArray.length); 
          setLoadingError(null);
      } else {
          setLoadingError("Invalid or empty custom array input. Please use comma-separated integers (e.g., 5,12,3,40).");
      }
  };


  // --- 1. Fetch Data on Array/Algorithm Change ---
  useEffect(() => {
    if (inputArray.length === 0) return;

    // Skip API call for unsupported/placeholder algorithms
    if (!isSupportedAlgorithm) {
        setSteps(null);
        setCurrentStepIndex(0);
        return; 
    }

    const testData = {
      algorithm: selectedAlgorithm, 
      array: inputArray 
    };

    // Reset state before fetching
    setSteps(null);
    setCurrentStepIndex(0);
    setLoadingError(null);

    fetch('/api/visualize', { 
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(testData),
    })
    .then(response => {
        if (!response.ok) {
            throw new Error(`HTTP error! status: ${response.status}`);
        }
        return response.json();
    })
    .then(data => {
      console.log(`SUCCESS! Data received from Flask for ${selectedAlgorithm}:`, data);
      setSteps(data);
    })
    .catch(error => {
        console.error('API Communication Failed:', error);
        setLoadingError(`Failed to fetch data: ${error.message}. Check Flask server logs.`);
    });

  }, [selectedAlgorithm, inputArray, isSupportedAlgorithm]); // Reruns when algorithm OR array changes

  // --- 2. Animation Timer Logic ---
  useEffect(() => {
    if (!steps || currentStepIndex >= steps.length - 1 || !isSupportedAlgorithm) return;

    const intervalId = setInterval(() => {
      setCurrentStepIndex(prevIndex => prevIndex + 1);
    }, ANIMATION_SPEED_MS); 

    return () => clearInterval(intervalId); 

  }, [steps, currentStepIndex, isSupportedAlgorithm]);

  // --- Algorithm Options Structure ---
  const algorithmOptions = [
    { label: "--- Sorting Algorithms ---", value: "header-sort", disabled: true },
    { label: "Bubble Sort (O(n²)) - Implemented", value: "bubblesort" },
    { label: "Merge Sort (O(n log n)) - Implemented", value: "mergesort" },
    { label: "Selection Sort (O(n²))", value: "selectionsort" },
    { label: "Insertion Sort (O(n²))", value: "insertionsort" },
    { label: "Quick Sort (O(n log n))", value: "quicksort" },
    { label: "Heap Sort (O(n log n))", value: "heapsort" },
    { label: "Shell Sort (O(n log² n))", value: "shellsort" },
    { label: "Timsort (Hybrid)", value: "timsort" },
    { label: "Introsort (Hybrid)", value: "introsort" },
    { label: "Counting Sort (Specialized)", value: "countingsort" },
    { label: "Radix Sort (Specialized)", value: "radixsort" },
    { label: "Bucket Sort (Specialized)", value: "bucketsort" },
    { label: "--- Search Algorithms ---", value: "header-search", disabled: true },
    { label: "Linear Search (O(n))", value: "linearsearch" },
    { label: "Binary Search (O(log n))", value: "binarysearch" },
    { label: "Ternary Search (O(log n))", value: "ternarysearch" },
    { label: "Jump Search (O(sqrt(n)))", value: "jumpsearch" },
    { label: "Interpolation Search (O(log log n))", value: "interpolationsearch" },
    { label: "Exponential Search (O(log n))", value: "exponentialsearch" },
    { label: "Hash Search (O(1) average)", value: "hashsearch" },
  ];


  return (
    <div style={{ padding: '20px' }}>
      <h1>Sorting & Search Visualizer</h1>

      {/* CONTROL PANEL */}
      <div style={{ marginBottom: '20px', display: 'flex', flexWrap: 'wrap', gap: '20px', alignItems: 'center' }}>

          {/* Algorithm Selector */}
          <div>
              <label htmlFor="algo-select" style={{ marginRight: '10px', fontWeight: 'bold' }}>Algorithm:</label>
              <select 
                  id="algo-select" 
                  value={selectedAlgorithm} 
                  onChange={(e) => setSelectedAlgorithm(e.target.value)}>
                  {algorithmOptions.map(option => (
                      <option 
                          key={option.value} 
                          value={option.value} 
                          disabled={option.disabled}>
                          {option.label}
                      </option>
                  ))}
              </select>
          </div>

          {/* Custom Array Input */}
          <div style={{ display: 'flex', gap: '10px', alignItems: 'center' }}>
            <input
                type="text"
                placeholder="Custom Array (e.g., 5,12,3,40)"
                value={customInputData}
                onChange={(e) => setCustomInputData(e.target.value)}
                style={{ padding: '8px', minWidth: '250px' }}
            />
            <button onClick={handleLoadCustomArray} style={{ padding: '8px 15px', cursor: 'pointer', backgroundColor: '#4CAF50', color: 'white', border: 'none' }}>
                Load Custom Array
            </button>
          </div>

          {/* Array Size Slider (Only affects random generation) */}
          <div>
              <label htmlFor="size-slider" style={{ marginRight: '10px', fontWeight: 'bold' }}>Array Size ({arraySize}):</label>
              <input 
                  id="size-slider"
                  type="range"
                  min="5" 
                  max="50" 
                  value={arraySize}
                  onChange={(e) => setArraySize(Number(e.target.value))} 
              />
          </div>

          {/* New Random Array Button */}
          <button onClick={() => handleGenerateNewArray()} style={{ padding: '8px 15px', cursor: 'pointer' }}>
              Generate New Random Array
          </button>

      </div>

      {loadingError && <p style={{color: 'red'}}>Error: {loadingError}</p>}

      {/* Visualization Container */}
      <div 
        style={{ 
          display: 'flex', 
          justifyContent: 'center', 
          alignItems: 'flex-end', 
          height: '400px',
          width: '100%',
          borderBottom: '2px solid #ccc'
        }}>

        {isSupportedAlgorithm ? currentArray.map((value, index) => (
          <ArrayBar 
            key={index}
            value={value}
            height={(value / MAX_ARRAY_VALUE) * 380} 
            width={(800 / arraySize) * 0.8}
            isHighlighted={highlightIndices.includes(index)}
          />
        )) : <h3 style={{marginTop: '150px', color: '#888'}}>Visualization not available for {selectedAlgorithm.toUpperCase().replace(/SEARCH|SORT/g, ' ')}. Please select Bubble Sort or Merge Sort.</h3>}
      </div>

      {/* Status Bar & Pseudocode Area */}
      <div style={{ marginTop: '20px', display: 'flex', gap: '50px' }}>

          {/* Status Bar */}
          <div style={{ flex: 1 }}>
              <h2>Status</h2>
              {isSupportedAlgorithm ? (
                  <>
                      {!steps && <p>Generating steps...</p>}
                      {steps && isSortingComplete && 
                          <h3 style={{color: '#4CAF50'}}>Sorting Complete! (Total Steps: {steps.length})</h3>}
                      {steps && !isSortingComplete && currentStep &&
                          <p>Step {currentStepIndex + 1} of {steps.length}. Action: **{currentStep.action}**</p>}
                  </>
              ) : (
                  <p style={{color: 'orange'}}>Algorithm implementation coming soon!</p>
              )}
          </div>

          {/* Pseudocode Area */}
          <div style={{ flex: 1, borderLeft: '1px solid #ccc', paddingLeft: '20px' }}>
              <h2>Pseudocode: {selectedAlgorithm}</h2>
              <div style={{ backgroundColor: '#f4f4f4', padding: '10px', borderRadius: '5px', fontSize: '14px', fontFamily: 'monospace', whiteSpace: 'pre-wrap' }}>
                {currentPseudocode ? currentPseudocode.map((line, index) => (
                    <div key={index}>{line}</div>
                )) : <p>// Pseudocode not yet available for this algorithm.</p>}
              </div>
          </div>

      </div>

      {/* DEVELOPER CREDIT LINE */}
      <div style={{ marginTop: '50px', paddingTop: '10px', borderTop: '1px solid #eee', textAlign: 'center', fontSize: '12px', color: '#666' }}>
          Developed by: **Shubham Jana** | Contact: <a href="mailto:devshubhamjana@gmail.com">**devshubhamjana@gmail.com**</a>
      </div>

    </div>
  );
}

export default App;
