        <BSTVisualizer />
      )}

      {/* DEVELOPER CREDIT LINE (Always visible) */}
      <div style={{ marginTop: '50px', paddingTop: '10px', borderTop: '1px solid #eee', textAlign: 'center', fontSize: '12px', color: '#666' }}>
          Developed by: **Shubham Jana** | Contact: <a href="mailto:devshubhamjana@gmail.com">**devshubhamjana@gmail.com**</a>
      </div>

    </div>
  );
}

