// frontend/src/App.js

import React, { useState, useEffect } from 'react';
// import ArrayBar from './components/ArrayBar'; // Commented out for now to focus on the API call

const INITIAL_ARRAY = [10, 4, 15, 2, 7, 18]; // Array for testing

function App() {
  const [steps, setSteps] = useState(null);
  const [loadingError, setLoadingError] = useState(null);

  const testData = {
    algorithm: "bubblesort", 
    array: INITIAL_ARRAY 
  };

  useEffect(() => {
    // The fetch URL uses the proxy defined in package.json
    fetch('/api/visualize', { 
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(testData),
    })
    .then(response => {
        if (!response.ok) {
            // Check for non-200 status codes (like 403, 400, 500)
            throw new Error(`HTTP error! status: ${response.status}`);
        }
        return response.json();
    })
    .then(data => {
      console.log("SUCCESS! Data received from Flask:", data);
      setSteps(data);
      setLoadingError(null);
    })
    .catch(error => {
        console.error('API Communication Failed:', error);
        setLoadingError(`Failed to fetch data: ${error.message}. Check Flask server logs.`);
    });
  }, []);

  return (
    <div className="App">
      <h1>Algorithm Visualizer</h1>
      {loadingError && <p style={{color: 'red'}}>{loadingError}</p>}
      {steps ? (
        <p>SUCCESS: Successfully received {steps.length} steps from Flask!</p>
      ) : (
        <p>Loading steps...</p>
      )}
      {/* Visualization will go here */}
    </div>
  );
}

export default App;
