import React, { useState, useEffect, useCallback, useRef } from 'react';

// --- Components ---

/**
 * ArrayBar Component: Displays a single element in the array visualization.
 * It handles color coding based on the algorithm state.
 */
const ArrayBar = React.memo(({ value, height, width, isHighlighted, isPivot, isSorted, isSearchMid, isSearchRange, isFound, isLinearCheck }) => {

    // Default color is Blue
    let color = 'bg-blue-500'; 
    let opacity = 'opacity-100';

    // --- Search Mode Logic ---
    if (isSearchMid || isSearchRange || isFound) {
        // Elements in the current search range (Binary Search)
        color = 'bg-blue-400'; 
        opacity = 'opacity-100';

        if (isSearchMid) {
            // Mid element (Binary Search)
            color = 'bg-amber-500'; // Amber/Mid
        }

        if (isFound) {
            // Found element (Both Search Algorithms)
            color = 'bg-emerald-500'; // Emerald/Found
        }
    } else if (isSearchMid === false && isSearchRange === false && value && (isSearchRange !== undefined)) { 
        // Excluded elements during Binary Search (isSearchRange check ensures this only applies to BS)
        opacity = 'opacity-30';
        color = 'bg-gray-400';
    }

    // Linear Search Specific Color
    if (isLinearCheck) {
        color = 'bg-red-500'; // Red for comparison check
        if (isFound) {
            color = 'bg-emerald-500'; // Emerald/Found
        }
    }


    // --- Sorting Mode Logic (Applied if not in active search highlight) ---
    if (isSorted) {
        color = 'bg-green-500';
        opacity = 'opacity-100';
    } else if (isPivot && !isSearchRange) {
        color = 'bg-purple-600'; // Purple for Pivot/MinIndex
    } else if (isHighlighted && !isSearchRange && !isSearchMid && !isLinearCheck) {
        color = 'bg-orange-500'; // Orange for comparison/swap
    }

    const finalWidth = width > 0 ? `${width}px` : 'auto';

    return (
        <div 
            className={`transition-all duration-100 ease-linear rounded-t-sm shadow-md flex items-end justify-center text-white font-bold relative ${color} ${opacity}`}
            style={{
                height: `${height}px`,
                width: finalWidth,
                margin: '0 1px',
                fontSize: width > 20 ? '10px' : '8px',
            }}
            title={`Value: ${value}`}
        >
            {/* Show value for bars wide enough */}
            {width > 20 && value}
        </div>
    );
});

/**
 * BSTVisualizer Component: Placeholder for the Binary Search Tree visualization view.
 * Note: The rendering here is simplified due to the constraints of a flat container.
 */
const BSTVisualizer = () => {
    const [treeData, setTreeData] = useState([]);
    const [inputValue, setInputValue] = useState('');
    const [message, setMessage] = useState('Enter a number to build the BST.');

    const insertNode = useCallback((data, value) => {
        if (!data) return { value: value, left: null, right: null };
        if (value < data.value) {
            data.left = insertNode(data.left, value);
        } else if (value > data.value) {
            data.right = insertNode(data.right, value);
        }
        return data;
    }, []);

    const handleInsert = () => {
        const num = parseInt(inputValue);
        if (isNaN(num)) {
            setMessage('Please enter a valid number.');
            return;
        }

        let newTreeData = [...treeData];
        if (newTreeData.length === 0) {
            newTreeData.push({ value: num, left: null, right: null });
        } else {
            newTreeData = [insertNode(newTreeData[0], num)];
        }

        setTreeData(newTreeData);
        setInputValue('');
        setMessage(`Inserted ${num}. Try inserting another number.`);
    };

    // A simplified text-based visualizer is used here since recursive rendering in a flat container is hard.
    const renderTreeSimplified = (node) => {
         if (!node) return ' (Empty)';
         return (
            <span className="font-mono text-sm">
                <span className="text-teal-600 font-bold">[{node.value}]</span>
                <span className="ml-1 text-gray-500">L:</span>
                <span className="ml-0.5">{renderTreeSimplified(node.left)}</span>
                <span className="ml-3 text-gray-500">R:</span>
                <span className="ml-0.5">{renderTreeSimplified(node.right)}</span>
            </span>
         );
    };

    return (
        <div className="p-4 md:p-8 bg-white rounded-lg shadow-xl mt-6">
            <h1 className="text-2xl font-extrabold text-gray-800 mb-2">Binary Search Tree Visualizer</h1>
            <p className="text-sm text-gray-600 mb-4">{message}</p>

            <div className="flex flex-col sm:flex-row gap-3 mb-6">
                <input
                    type="number"
                    value={inputValue}
                    onChange={(e) => setInputValue(e.target.value)}
                    placeholder="Enter number (0-999)"
                    className="p-3 border border-gray-300 rounded-lg focus:ring-blue-500 focus:border-blue-500 min-w-40"
                    min="0"
                    max="999"
                />
                <button 
                    onClick={handleInsert} 
                    className="px-6 py-3 bg-blue-600 text-white font-semibold rounded-lg shadow-md hover:bg-blue-700 transition duration-150 ease-in-out disabled:opacity-50"
                    disabled={!inputValue || isNaN(parseInt(inputValue))}
                >
                    Insert Node
                </button>
            </div>

            <div className="min-h-[400px] bg-gray-50 p-5 border border-gray-200 rounded-lg overflow-auto">
                <h3 className="text-lg font-semibold text-gray-700 mb-3 border-b pb-2">Tree Structure (Simplified Console Output)</h3>
                {treeData.length > 0 ? (
                    <div className="whitespace-nowrap pt-5">
                       {renderTreeSimplified(treeData[0])}
                    </div>
                ) : (
                    <p className="mt-20 text-center text-gray-500 italic">Tree is empty. Insert the first node to begin.</p>
                )}
            </div>
        </div>
    );
};


// --- Constants ---
const DEFAULT_ARRAY_SIZE = 15;
const MAX_ARRAY_VALUE = 400; 
const MIN_ARRAY_VALUE = 10;
const ANIMATION_SPEED_MS = 80; 

// --- Algorithm Pseudocode ---
const PSEUDOCODE = {
    bubblesort: [
        "function bubbleSort(array):",
        "  n = array.length",
        "  for i from 0 to n - 2:",
        "    for j from 0 to n - 2 - i:",
        "      // Compare & Highlight array[j] and array[j+1]",
        "      if array[j] > array[j+1]:",
        "        // Swap elements",
        "        swap(array[j], array[j+1])",
        "  return array"
    ],
    mergesort: [
        "function mergeSort(array):",
        "  if array.length <= 1: return array",
        "  mid = floor(array.length / 2)",
        "  left = mergeSort(array[0...mid])",
        "  right = mergeSort(array[mid...n])",
        "  return merge(left, right)",
        "",
        "function merge(left, right):",
        "  // Merging sub-arrays and placing back into the original array...",
        "  result = []",
        "  while left & right are not empty:",
        "    if left[0] <= right[0]:",
        "      result.push(left.shift())",
        "    else:",
        "      result.push(right.shift())",
        "  return result + left + right"
    ],
    quicksort: [
        "function quickSort(array, low, high):",
        "  if low < high:",
        "    // Partition the array",
        "    pi = partition(array, low, high)",
        "    // Recursively sort left and right sub-arrays",
        "    quickSort(array, low, pi - 1)",
        "    quickSort(array, pi + 1, high)",
        "",
        "function partition(array, low, high):",
        "  pivot = array[high] // Choose the last element as pivot",
        "  i = low - 1",
        "  for j from low to high - 1:",
        "    if array[j] < pivot:",
        "      i++",
        "      swap(array[i], array[j])",
        "  swap(array[i+1], array[high])",
        "  return i + 1"
    ],
    shellsort: [
        "function shellSort(array):",
        "  n = array.length",
        "  gap = floor(n / 2)",
        "  while gap > 0:",
        "    // Perform gapped insertion sort",
        "    for i from gap to n - 1:",
        "      temp = array[i]",
        "      j = i",
        "      while j >= gap and array[j - gap] > temp:",
        "        // Shift elements (Highlight comparison and shift)",
        "        array[j] = array[j - gap]",
        "        j = j - gap",
        "      // Place temp (Highlight placement)",
        "      array[j] = temp",
        "    gap = floor(gap / 2) // Reduce the gap"
    ],
    selectionsort: [
        "function selectionSort(array):",
        "  n = array.length",
        "  for i from 0 to n - 2:",
        "    // Assume current position i holds the minimum",
        "    minIndex = i",
        "    for j from i + 1 to n - 1:",
        "      // Highlight comparison between array[j] and array[minIndex]",
        "      if array[j] < array[minIndex]:",
        "        // Update the index of the minimum element",
        "        minIndex = j",
        "    // If a smaller element was found, swap it into position i",
        "    if minIndex is not i:",
        "      swap(array[i], array[minIndex])",
        "    // Position i is finalized",
        "  return array"
    ],
    binarysearch: [
        "function binarySearch(array, target):",
        "  // Array MUST be sorted",
        "  low = 0",
        "  high = array.length - 1",
        "  while low <= high:",
        "    mid = floor((low + high) / 2)",
        "    // Highlight mid element (array[mid])",
        "    if array[mid] == target:",
        "      return mid",
        "    else if array[mid] < target:",
        "      // Search right (New Low = mid + 1)",
        "      low = mid + 1",
        "    else:",
        "      // Search left (New High = mid - 1)",
        "      high = mid - 1",
        "  return -1"
    ],
    linearsearch: [
        "function linearSearch(array, target):",
        "  n = array.length",
        "  for i from 0 to n - 1:",
        "    // Highlight array[i]",
        "    if array[i] == target:",
        "      return i",
        "  return -1"
    ]
};

// --- Helper Functions ---

const generateRandomArray = (size) => {
    return Array.from({ length: size }, () => 
        Math.floor(Math.random() * (MAX_ARRAY_VALUE - MIN_ARRAY_VALUE + 1)) + MIN_ARRAY_VALUE
    );
};

const parseCustomArray = (inputString) => {
    const elements = inputString.split(',')
        .map(s => {
            const num = parseInt(s.trim());
            return isNaN(num) || num <= 0 ? null : Math.min(num, MAX_ARRAY_VALUE);
        })
        .filter(n => n !== null);
    return elements;
};

// --- CLIENT-SIDE ALGORITHM LOGIC (Generators for visualization steps) ---
const getBubbleSortSteps = (initialArray) => { 
    const array = [...initialArray];
    const steps = [];
    const n = array.length;
    let sortedIndices = [];

    for (let i = 0; i < n - 1; i++) {
        let swapped = false;
        for (let j = 0; j < n - 1 - i; j++) {
            steps.push({
                array: [...array], 
                highlight_indices: [j, j + 1], 
                action: `Comparing array[${j}] and array[${j + 1}]`,
                sorted_indices: [...sortedIndices]
            });

            if (array[j] > array[j + 1]) {
                [array[j], array[j + 1]] = [array[j + 1], array[j]];
                swapped = true;
                steps.push({
                    array: [...array], 
                    highlight_indices: [j, j + 1], 
                    action: `Swapping ${array[j+1]} and ${array[j]}`, 
                    sorted_indices: [...sortedIndices]
                });
            }
        }

        if (n - 1 - i >= 0) {
            sortedIndices.push(n - 1 - i);
        }

        if (!swapped) {
            // Mark the rest as sorted if no swap occurred
            for (let k = 0; k < n - 1 - i; k++) {
                if (!sortedIndices.includes(k)) {
                     sortedIndices.push(k);
                }
            }
            break;
        }
    }

    const finalSortedIndices = Array.from({ length: n }, (_, i) => i);
    steps.push({
        array: [...array], 
        highlight_indices: [], 
        action: "Finished Bubble Sort",
        sorted_indices: finalSortedIndices
    });

    return steps;
};

const getMergeSortSteps = (initialArray) => { 
    const array = [...initialArray];
    const steps = [];

    const merge = (arr, start, mid, end) => {
        const left = arr.slice(start, mid);
        const right = arr.slice(mid, end);
        let i = 0, j = 0, k = start;

        steps.push({
            array: [...array],
            highlight_indices: Array.from({ length: end - start }, (_, idx) => start + idx),
            action: `Merging sub-arrays from index ${start} to ${end - 1}`,
            sorted_indices: []
        });

        while (i < left.length && j < right.length) {
            if (left[i] <= right[j]) {
                array[k] = left[i];
                i++;
            } else {
                array[k] = right[j];
                j++;
            }
            k++;

            steps.push({
                array: [...array],
                highlight_indices: [k - 1], 
                action: `Placing element at index ${k - 1}`,
                sorted_indices: []
            });
        }

        while (i < left.length) {
            array[k] = left[i];
            i++; k++;
            steps.push({
                array: [...array],
                highlight_indices: [k - 1],
                action: `Placing remaining element from left half at ${k - 1}`,
                sorted_indices: []
            });
        }
        while (j < right.length) {
            array[k] = right[j];
            j++; k++;
            steps.push({
                array: [...array],
                highlight_indices: [k - 1],
                action: `Placing remaining element from right half at ${k - 1}`,
                sorted_indices: []
            });
        }

        steps.push({
            array: [...array],
            highlight_indices: [],
            action: `Merged segment complete from index ${start} to ${end - 1}`,
            sorted_indices: [] 
        });
    };

    const mergeSortHelper = (arr, start, end) => {
        if (end - start <= 1) return;

        const mid = Math.floor((start + end) / 2);
        mergeSortHelper(arr, start, mid);
        mergeSortHelper(arr, mid, end);
        merge(arr, start, mid, end);
    };

    mergeSortHelper(array, 0, array.length);

    const finalSortedIndices = Array.from({ length: array.length }, (_, i) => i);
    steps.push({
        array: [...array], 
        highlight_indices: [], 
        action: "Finished Merge Sort",
        sorted_indices: finalSortedIndices
    });

    return steps;
};

const getQuickSortSteps = (initialArray) => { 
    const array = [...initialArray];
    const steps = [];

    const recordStep = (action, highlight_indices = [], pivot_index = null, sorted_indices = []) => {
        steps.push({
            array: [...array],
            action: action,
            highlight_indices: highlight_indices,
            pivot_index: pivot_index,
            sorted_indices: [...sorted_indices]
        });
    };

    const partition = (arr, low, high, sortedIndices) => {
        const pivot = arr[high]; 
        let i = low - 1; 

        recordStep(`Selecting pivot ${pivot} at index ${high}`, [], high, sortedIndices);

        for (let j = low; j <= high - 1; j++) {
            recordStep(`Comparing ${arr[j]} at ${j} with pivot ${pivot}`, [j], high, sortedIndices);

            if (arr[j] < pivot) {
                i++;
                [arr[i], arr[j]] = [arr[j], arr[i]];
                recordStep(`Swapping ${arr[i]} at ${i} and ${arr[j]} at ${j}`, [i, j], high, sortedIndices);
            }
        }

        const pivotIndex = i + 1;
        [arr[pivotIndex], arr[high]] = [arr[high], arr[pivotIndex]];

        recordStep(`Pivot ${pivot} placed correctly at index ${pivotIndex}`, [pivotIndex], null, [...sortedIndices, pivotIndex]);

        return pivotIndex;
    };

    const sortedIndicesTracker = [];

    const quickSortHelper = (arr, low, high) => {
        if (low < high) {
            const pi = partition(arr, low, high, sortedIndicesTracker);

            if (!sortedIndicesTracker.includes(pi)) {
                sortedIndicesTracker.push(pi);
            }

            quickSortHelper(arr, low, pi - 1);
            quickSortHelper(arr, pi + 1, high);
        } else if (low === high && low >= 0) {
            if (!sortedIndicesTracker.includes(low)) {
                sortedIndicesTracker.push(low);
                recordStep(`Sub-array of size 1 at index ${low} is sorted.`, [], null, sortedIndicesTracker);
            }
        }
    };

    quickSortHelper(array, 0, array.length - 1);

    const finalSortedIndices = Array.from({ length: array.length }, (_, i) => i);
    steps.push({
        array: [...array], 
        action: "Finished Quick Sort",
        highlight_indices: [],
        pivot_index: null,
        sorted_indices: finalSortedIndices
    });

    return steps;
};

const getShellSortSteps = (initialArray) => { 
    const array = [...initialArray];
    const steps = [];
    const n = array.length;
    let gap = Math.floor(n / 2);
    let sortedIndices = [];

    const recordStep = (action, highlight_indices = [], current_gap = gap, sorted_indices = []) => {
        steps.push({
            array: [...array],
            action: action,
            highlight_indices: highlight_indices,
            gap: current_gap,
            sorted_indices: [...sorted_indices]
        });
    };

    while (gap > 0) {
        recordStep(`Starting pass with Gap = ${gap}`, [], gap, sortedIndices);

        for (let i = gap; i < n; i++) {
            const temp = array[i];
            let j = i;

            recordStep(`Selecting element ${temp} at index ${i}. Gap = ${gap}`, [i], gap, sortedIndices);

            while (j >= gap && array[j - gap] > temp) {
                recordStep(`Comparing ${array[j - gap]} at ${j - gap} and ${temp} (to be placed at ${j}). Gap = ${gap}`, [j, j - gap], gap, sortedIndices);

                array[j] = array[j - gap];

                recordStep(`Shifting ${array[j - gap]} to ${j}. Gap = ${gap}`, [j], gap, sortedIndices);

                j -= gap;
            }

            array[j] = temp;

            recordStep(`Placing ${temp} at index ${j}. Gap = ${gap}`, [j], gap, sortedIndices);
        }

        gap = Math.floor(gap / 2);
    }

    const finalSortedIndices = Array.from({ length: n }, (_, i) => i);
    recordStep("Finished Shell Sort", [], 0, finalSortedIndices);

    return steps;
};

const getSelectionSortSteps = (initialArray) => { 
    const array = [...initialArray];
    const steps = [];
    const n = array.length;
    let sortedIndices = [];

    const recordStep = (action, highlight_indices = [], min_index = null, sorted_indices = []) => {
        steps.push({
            array: [...array],
            action: action,
            highlight_indices: highlight_indices,
            pivot_index: min_index, 
            sorted_indices: [...sorted_indices]
        });
    };

    for (let i = 0; i < n - 1; i++) {
        let minIndex = i;

        recordStep(`Starting pass ${i + 1}. Assume minimum is ${array[i]} at index ${i}.`, [], minIndex, sortedIndices);

        for (let j = i + 1; j < n; j++) {
            recordStep(`Comparing ${array[j]} at ${j} with current minimum ${array[minIndex]} at ${minIndex}.`, [j], minIndex, sortedIndices);

            if (array[j] < array[minIndex]) {
                minIndex = j;
                recordStep(`New minimum found: ${array[minIndex]} at index ${minIndex}.`, [j], minIndex, sortedIndices);
            }
        }

        if (minIndex !== i) {
            [array[i], array[minIndex]] = [array[minIndex], array[i]];
            recordStep(`Swapping ${array[i]} (new smallest) with ${array[minIndex]} (current position) to place smallest element at ${i}.`, [i, minIndex], null, sortedIndices);
        } else {
             recordStep(`Element at ${i} is already the smallest in the unsorted portion.`, [], null, sortedIndices);
        }

        sortedIndices.push(i);
        recordStep(`Position ${i} is finalized (Sorted).`, [], null, sortedIndices);
    }

    if (n > 0 && !sortedIndices.includes(n - 1)) {
        sortedIndices.push(n - 1);
    }

    const finalSortedIndices = Array.from({ length: n }, (_, k) => k);
    recordStep("Finished Selection Sort", [], null, finalSortedIndices);

    return steps;
};

const getBinarySearchSteps = (arr, target) => { 
    const steps = [];
    const array = [...arr];

    steps.push({
        array: [...array],
        action: `Search started for target ${target}. Array must be sorted.`,
        low: 0,
        high: array.length - 1,
        mid: -1,
        found: false,
        search_target: target,
        highlight_indices: [], 
        isSearch: true,
    });

    let low = 0;
    let high = array.length - 1;
    let found = false;
    let mid = -1;

    while (low <= high) {
        mid = Math.floor((low + high) / 2);

        steps.push({
            array: [...array],
            action: `Checking mid element at index ${mid}: Value is ${array[mid]}`,
            low: low,
            high: high,
            mid: mid,
            found: false,
            search_target: target,
            highlight_indices: [mid],
            isSearch: true,
        });

        if (array[mid] === target) {
            found = true;
            break;
        } else if (array[mid] < target) {
            low = mid + 1;
            steps.push({
                array: [...array],
                action: `Target (${target}) is greater than ${array[mid]}. Searching right half (New Low: ${low}).`,
                low: low,
                high: high,
                mid: mid,
                found: false,
                search_target: target,
                highlight_indices: [],
                isSearch: true,
            });
        } else {
            high = mid - 1;
            steps.push({
                array: [...array],
                action: `Target (${target}) is less than ${array[mid]}. Searching left half (New High: ${high}).`,
                low: low,
                high: high,
                mid: mid,
                found: false,
                search_target: target,
                highlight_indices: [],
                isSearch: true,
            });
        }
    }

    if (found) {
        steps.push({
            array: [...array],
            action: `SUCCESS! Target ${target} found at index ${mid}. Search complete.`,
            low: low,
            high: high,
            mid: mid,
            found: true,
            search_target: target,
            highlight_indices: [mid],
            isSearch: true,
        });
    } else {
        steps.push({
            array: [...array],
            action: `FAILURE! Target ${target} not found. Low (${low}) > High (${high}).`,
            low: low,
            high: high,
            mid: -1,
            found: false,
            search_target: target,
            highlight_indices: [],
            isSearch: true,
        });
    }

    return steps;
};

const getLinearSearchSteps = (arr, target) => { 
    const steps = [];
    const array = [...arr];
    const n = array.length;
    let found = false;
    let foundIndex = -1;

    steps.push({
        array: [...array],
        action: `Linear Search started for target ${target}.`,
        current_index: -1,
        found: false,
        search_target: target,
        highlight_indices: [],
        isLinearSearch: true,
    });

    for (let i = 0; i < n; i++) {

        steps.push({
            array: [...array],
            action: `Comparing element at index ${i}: Value is ${array[i]}`,
            current_index: i,
            found: false,
            search_target: target,
            highlight_indices: [i],
            isLinearSearch: true,
        });

        if (array[i] === target) {
            found = true;
            foundIndex = i;
            break;
        }

        steps.push({
            array: [...array],
            action: `Value ${array[i]} does not match ${target}. Moving to next index.`,
            current_index: i,
            found: false,
            search_target: target,
            highlight_indices: [], 
            isLinearSearch: true,
        });
    }

    if (found) {
        steps.push({
            array: [...array],
            action: `SUCCESS! Target ${target} found at index ${foundIndex}. Search complete.`,
            current_index: foundIndex,
            found: true,
            search_target: target,
            highlight_indices: [foundIndex],
            isLinearSearch: true,
        });
    } else {
        steps.push({
            array: [...array],
            action: `FAILURE! Target ${target} not found after checking all elements. Search complete.`,
            current_index: n - 1,
            found: false,
            search_target: target,
            highlight_indices: [],
            isLinearSearch: true,
        });
    }

    return steps;
};

// --- Main App Component ---
export default function App() {
  const [currentView, setCurrentView] = useState('sort'); 

  // Sorting/Searching Visualizer States
  const [steps, setSteps] = useState(null);
  const [currentStepIndex, setCurrentStepIndex] = useState(0);
  const [selectedAlgorithm, setSelectedAlgorithm] = useState("binarysearch"); 
  const [loadingError, setLoadingError] = useState(null);
  const [inputArray, setInputArray] = useState(() => generateRandomArray(DEFAULT_ARRAY_SIZE));
  const [arraySize, setArraySize] = useState(DEFAULT_ARRAY_SIZE);
  const [customInputData, setCustomInputData] = useState("");
  const [speed, setSpeed] = useState(ANIMATION_SPEED_MS);

  // State for Search Algorithms
  const [searchTarget, setSearchTarget] = useState(50); 

  // Animation State
  const [isAnimating, setIsAnimating] = useState(false);
  const animationRef = useRef(null);

  // Derived States
  const currentStep = steps ? steps[currentStepIndex] : null;
  const currentArray = currentStep ? currentStep.array : inputArray; 
  const highlightIndices = currentStep ? currentStep.highlight_indices : [];
  const pivotIndex = currentStep ? currentStep.pivot_index : null;
  const sortedIndices = currentStep ? currentStep.sorted_indices || [] : [];
  const isSortingComplete = steps && currentStepIndex >= steps.length - 1; 
  const currentPseudocode = PSEUDOCODE[selectedAlgorithm];

  const isSearchAlgorithm = selectedAlgorithm === 'binarysearch' || selectedAlgorithm === 'linearsearch';
  const isSupportedAlgorithm = ["bubblesort", "mergesort", "quicksort", "shellsort", "selectionsort", "binarysearch", "linearsearch"].includes(selectedAlgorithm); 

  // Calculate bar width based on container width and number of elements
  const barWidth = Math.max(10, (800 / currentArray.length) - 2); // Max 800px width for visualization container

  // --- Handlers ---
  const handleGenerateNewArray = (size = arraySize) => {
      if (isAnimating) handleStartStopAnimation();
      const newArray = generateRandomArray(size);
      setInputArray(newArray);
      setCustomInputData(""); 
      setArraySize(size);
      if (isSearchAlgorithm && newArray.length > 0) {
          // Set a random element from the new array as the target
          setSearchTarget(newArray[Math.floor(Math.random() * newArray.length)]); 
      } else {
          setSearchTarget(50);
      }
  };

  const handleLoadCustomArray = () => {
      if (isAnimating) handleStartStopAnimation();
      const newArray = parseCustomArray(customInputData);
      if (newArray.length > 0 && newArray.length <= 50) {
          setInputArray(newArray);
          setArraySize(newArray.length); 
          setLoadingError(null);
          if (isSearchAlgorithm && newArray.length > 0) {
            setSearchTarget(newArray[Math.floor(Math.random() * newArray.length)]);
          } else {
            setSearchTarget(50);
          }
      } else if (newArray.length > 50) {
           setLoadingError("Maximum array size for custom input is 50 elements.");
      } else {
          setLoadingError("Invalid or empty custom array input. Please use comma-separated integers (e.g., 5,12,3,40).");
      }
  };

  const handleSearchClick = () => {
    // Re-trigger useEffect for search logic
    const currentTarget = Number(searchTarget);
    setSearchTarget(currentTarget);
    if (isAnimating) handleStartStopAnimation();
    setCurrentStepIndex(0);
    // Note: The main logic runs in useEffect below
  };


  // --- Animation Control Logic ---

  // Function to handle the step-by-step animation
  const animate = useCallback(() => {
    if (!steps || currentStepIndex >= steps.length - 1) {
        setIsAnimating(false);
        return;
    }

    const nextStepIndex = currentStepIndex + 1;
    setCurrentStepIndex(nextStepIndex);

    animationRef.current = setTimeout(() => {
        animate();
    }, speed);

  }, [currentStepIndex, steps, speed]);

  // Start/Stop Animation handler
  const handleStartStopAnimation = useCallback(() => {
    if (isAnimating) {
        if (animationRef.current) {
            clearTimeout(animationRef.current);
        }
        setIsAnimating(false);
    } else {
        if (isSortingComplete) {
            setCurrentStepIndex(0);
        }
        setIsAnimating(true);
    }
  }, [isAnimating, isSortingComplete]);

  // Effect to control the animation loop start/stop
  useEffect(() => {
    if (isAnimating) {
        animate();
    } else if (animationRef.current) {
        clearTimeout(animationRef.current);
    }

    // Cleanup function
    return () => {
        if (animationRef.current) {
            clearTimeout(animationRef.current);
        }
    };
  }, [isAnimating, animate]); 


  // Effect to recalculate steps whenever the array, algorithm, or target changes
  useEffect(() => {
    if (isAnimating) {
        handleStartStopAnimation(); 
    }

    // Reset state
    setCurrentStepIndex(0);
    setSteps(null);
    setLoadingError(null);

    if (inputArray.length === 0) {
        setLoadingError("The array is empty. Please generate or enter an array.");
        return;
    }

    // Logic for search algorithms requiring sorting
    let arrayToSort = [...inputArray];
    if (selectedAlgorithm === 'binarysearch') {
        // Binary search MUST have a sorted array
        const isAlreadySorted = arrayToSort.every((val, i, arr) => i === 0 || val >= arr[i - 1]);

        if (!isAlreadySorted) {
             // Sort it internally for Binary Search demonstration
             arrayToSort.sort((a, b) => a - b);
             // Update the array state to show the sorted version
             setInputArray(arrayToSort); 
             setLoadingError("Binary Search requires a sorted array. The array has been sorted automatically.");
        }
    } else if (selectedAlgorithm === 'linearsearch') {
        // Linear search uses the original array
        arrayToSort = [...inputArray];
    }


    try {
        let newSteps = [];
        const roundedSearchTarget = Math.round(Number(searchTarget));

        // Dispatch to the correct algorithm step generator
        switch (selectedAlgorithm) {
            case 'bubblesort':
                newSteps = getBubbleSortSteps(arrayToSort);
                break;
            case 'mergesort':
                newSteps = getMergeSortSteps(arrayToSort);
                break;
            case 'quicksort':
                newSteps = getQuickSortSteps(arrayToSort);
                break;
            case 'shellsort':
                newSteps = getShellSortSteps(arrayToSort);
                break;
            case 'selectionsort':
                newSteps = getSelectionSortSteps(arrayToSort);
                break;
            case 'binarysearch':
                // Note: arrayToSort is already internally sorted here if needed
                newSteps = getBinarySearchSteps(arrayToSort, roundedSearchTarget);
                break;
            case 'linearsearch':
                newSteps = getLinearSearchSteps(arrayToSort, roundedSearchTarget);
                break;
            default:
                setLoadingError("Selected algorithm is not supported.");
                return;
        }

        if (newSteps.length > 0) {
            setSteps(newSteps);
        } else {
            setLoadingError("Algorithm produced no steps. Check input validity.");
        }

    } catch (error) {
        console.error("Error generating algorithm steps:", error);
        setLoadingError("An error occurred during step generation. See console for details.");
    }

  }, [inputArray, selectedAlgorithm, searchTarget]); // DEPENDENCY ADDED & EFFECT COMPLETED

  // --- Render Functions ---

  const renderAlgorithmVisualizer = () => {
    const totalSteps = steps ? steps.length : 0;
    const isPlaybackDisabled = !steps || isAnimating || steps.length < 2;
    const isStepBackDisabled = !steps || currentStepIndex === 0 || isAnimating;
    const isStepForwardDisabled = !steps || currentStepIndex === totalSteps - 1 || isAnimating;

    // Determine the current state for visualization
    const actionMessage = currentStep 
        ? currentStep.action 
        : (isSortingComplete 
            ? "Sorting Complete! Generate a new array to start over." 
            : `Ready to start ${selectedAlgorithm}. Press Play.`);

    return (
        <div className="p-4 md:p-8 bg-white rounded-lg shadow-xl mt-6">
            <h1 className="text-2xl font-extrabold text-gray-800 mb-2">
                {isSearchAlgorithm ? 'Search' : 'Sort'} Algorithm Visualizer: <span className="text-blue-600 capitalize">{selectedAlgorithm.replace('sort', ' Sort').replace('search', ' Search')}</span>
            </h1>

            <div className="flex flex-col lg:flex-row gap-6">

                {/* 1. CONTROLS */}
                <div className="lg:w-1/3 p-4 bg-gray-50 rounded-lg shadow-inner flex flex-col gap-4">
                    <h2 className="text-lg font-semibold text-gray-700 border-b pb-2">Configuration</h2>

                    {/* Algorithm Selector */}
                    <div className="flex flex-col">
                        <label htmlFor="algorithm-select" className="text-sm font-medium text-gray-700 mb-1">Select Algorithm</label>
                        <select 
                            id="algorithm-select"
                            value={selectedAlgorithm}
                            onChange={(e) => setSelectedAlgorithm(e.target.value)}
                            className="p-2 border border-gray-300 rounded-lg focus:ring-blue-500 focus:border-blue-500"
                        >
                            <optgroup label="Sorting Algorithms">
                                <option value="bubblesort">Bubble Sort</option>
                                <option value="selectionsort">Selection Sort</option>
                                <option value="mergesort">Merge Sort</option>
                                <option value="quicksort">Quick Sort</option>
                                <option value="shellsort">Shell Sort</option>
                            </optgroup>
                            <optgroup label="Searching Algorithms">
                                <option value="binarysearch">Binary Search (Requires Sorted Array)</option>
                                <option value="linearsearch">Linear Search</option>
                            </optgroup>
                        </select>
                    </div>

                    {/* Array Generation Controls */}
                    <div className="flex flex-col gap-2">
                        <label className="text-sm font-medium text-gray-700">Array Size ({arraySize} bars)</label>
                        <input
                            type="range"
                            min="5"
                            max="50"
                            value={arraySize}
                            onChange={(e) => setArraySize(Number(e.target.value))}
                            className="w-full h-2 bg-gray-200 rounded-lg appearance-none cursor-pointer range-sm"
                            disabled={isAnimating}
                        />
                        <button 
                            onClick={() => handleGenerateNewArray()} 
                            className="px-4 py-2 bg-purple-600 text-white font-semibold rounded-lg shadow-md hover:bg-purple-700 transition duration-150 ease-in-out disabled:opacity-50"
                            disabled={isAnimating}
                        >
                            Generate New Random Array
                        </button>
                    </div>

                    {/* Custom Array Input */}
                    <div className="flex flex-col gap-2">
                        <label className="text-sm font-medium text-gray-700">Custom Array (e.g., 20,5,12)</label>
                        <div className="flex gap-2">
                            <input
                                type="text"
                                value={customInputData}
                                onChange={(e) => setCustomInputData(e.target.value)}
                                placeholder="Comma-separated numbers"
                                className="p-2 border border-gray-300 rounded-lg flex-grow"
                                disabled={isAnimating}
                            />
                            <button 
                                onClick={handleLoadCustomArray} 
                                className="px-3 py-2 bg-teal-500 text-white text-sm rounded-lg hover:bg-teal-600 transition disabled:opacity-50"
                                disabled={isAnimating}
                            >
                                Load
                            </button>
                        </div>
                        {loadingError && <p className="text-red-500 text-xs mt-1">{loadingError}</p>}
                    </div>

                    {/* Search Target Input (Conditional) */}
                    {isSearchAlgorithm && (
                        <div className="flex flex-col gap-2 pt-2 border-t border-gray-200">
                            <label className="text-sm font-medium text-gray-700">Search Target</label>
                            <div className="flex gap-2">
                                <input
                                    type="number"
                                    value={Math.round(Number(searchTarget))}
                                    onChange={(e) => setSearchTarget(Number(e.target.value))}
                                    placeholder="Target Value"
                                    className="p-2 border border-gray-300 rounded-lg flex-grow"
                                    min={MIN_ARRAY_VALUE}
                                    max={MAX_ARRAY_VALUE}
                                    disabled={isAnimating}
                                />
                                <button 
                                    onClick={handleSearchClick} 
                                    className="px-3 py-2 bg-blue-500 text-white text-sm rounded-lg hover:bg-blue-600 transition disabled:opacity-50"
                                    disabled={isAnimating}
                                >
                                    Search
                                </button>
                            </div>
                        </div>
                    )}
                </div>


                {/* 2. VISUALIZATION AND PLAYBACK */}
                <div className="lg:w-2/3 flex flex-col gap-4">
                    <div className="bg-gray-800 p-4 rounded-lg shadow-xl min-h-[400px]">
                        <div className="flex items-end justify-center h-[350px] w-full" style={{ borderBottom: '2px solid #374151' }}>
                            {currentArray.map((value, index) => (
                                <ArrayBar
                                    key={index}
                                    value={value}
                                    height={value}
                                    width={barWidth}
                                    isHighlighted={highlightIndices.includes(index) && index !== pivotIndex}
                                    isPivot={index === pivotIndex}
                                    isSorted={sortedIndices.includes(index)}
                                    // Search specific props
                                    isSearchMid={isSearchAlgorithm && currentStep?.mid === index}
                                    isSearchRange={isSearchAlgorithm && index >= currentStep?.low && index <= currentStep?.high}
                                    isFound={isSearchAlgorithm && currentStep?.found && (currentStep?.mid === index || currentStep?.current_index === index)}
                                    isLinearCheck={selectedAlgorithm === 'linearsearch' && currentStep?.current_index === index}
                                />
                            ))}
                        </div>

                        <div className="text-white text-sm font-mono mt-3">
                            Step {currentStepIndex} of {totalSteps - 1} | 
                            <span className={`ml-2 font-bold ${currentStep?.found ? 'text-emerald-400' : 'text-orange-400'}`}>
                                {actionMessage}
                            </span>
                        </div>
                    </div>

                    {/* Playback Controls */}
                    <div className="flex justify-center items-center gap-4 bg-gray-50 p-3 rounded-lg shadow-md">
                        <button 
                            onClick={() => setCurrentStepIndex(Math.max(0, currentStepIndex - 1))}
                            disabled={isStepBackDisabled}
                            className="p-2 text-gray-700 rounded-full hover:bg-gray-200 transition disabled:opacity-30"
                            title="Previous Step"
                        >
                            <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M11 19l-7-7 7-7m8 14l-7-7 7-7" /></svg>
                        </button>

                        <button 
                            onClick={handleStartStopAnimation}
                            disabled={isPlaybackDisabled}
                            className={`p-3 rounded-full shadow-lg transition ${isAnimating ? 'bg-orange-500 hover:bg-orange-600' : 'bg-green-500 hover:bg-green-600'} text-white`}
                            title={isAnimating ? "Pause" : "Play"}
                        >
                            {isAnimating ? (
                                <svg xmlns="http://www.w3.org/2000/svg" className="h-8 w-8" fill="none" viewBox="0 0 24 24" stroke="currentColor"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M10 9v6m4-6v6m7-3a9 9 0 11-18 0 9 9 0 0118 0z" /></svg>
                            ) : (
                                <svg xmlns="http://www.w3.org/2000/svg" className="h-8 w-8" fill="none" viewBox="0 0 24 24" stroke="currentColor"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M14.752 11.168l-3.197-2.132A1 1 0 0010 9.87v4.263a1 1 0 001.555.832l3.197-2.132a1 1 0 000-1.664z" /><path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M21 12a9 9 0 11-18 0 9 9 0 0118 0z" /></svg>
                            )}
                        </button>

                        <button 
                            onClick={() => setCurrentStepIndex(Math.min(totalSteps - 1, currentStepIndex + 1))}
                            disabled={isStepForwardDisabled}
                            className="p-2 text-gray-700 rounded-full hover:bg-gray-200 transition disabled:opacity-30"
                            title="Next Step"
                        >
                            <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M13 5l7 7-7 7M5 5l7 7-7 7" /></svg>
                        </button>

                        <input
                            type="range"
                            min="10"
                            max="500"
                            value={500 - speed}
                            onChange={(e) => setSpeed(500 - Number(e.target.value))}
                            className="w-24 h-2 bg-gray-200 rounded-lg appearance-none cursor-pointer range-sm ml-4"
                            title={`Animation Speed: ${speed}ms`}
                        />
                    </div>
                </div>
            </div>

            {/* 3. PSEUDOCODE / INFO PANEL */}
            <div className="mt-6 p-4 bg-gray-700 text-white rounded-lg font-mono text-sm shadow-inner">
                <h3 className="font-bold text-lg mb-2 text-blue-300 border-b border-gray-600 pb-1">Pseudocode</h3>
                {currentPseudocode.map((line, index) => (
                    <p 
                        key={index}
                        className={`py-0.5 whitespace-pre-wrap ${
                            line.includes('//') ? 'text-gray-400 italic' : ''
                        } ${
                            currentStep && currentStep.action.includes(line.trim().split(':')[0]) 
                            ? 'bg-blue-800/70 border-l-4 border-yellow-400 pl-1' 
                            : 'hover:bg-gray-600'
                        }`}
                    >
                        {line}
                    </p>
                ))}
            </div>
        </div>
    );
  };

  // --- Main Render ---

  return (
    <div className="min-h-screen bg-gray-100 p-4 md:p-8 font-['Inter']">
        <div className="max-w-7xl mx-auto">

            {/* View Switcher */}
            <div className="flex justify-center mb-6 p-1 bg-white rounded-xl shadow-lg">
                <button 
                    onClick={() => setCurrentView('sort')}
                    className={`px-6 py-3 font-bold rounded-lg transition-all ${currentView === 'sort' ? 'bg-blue-600 text-white shadow-md' : 'text-gray-600 hover:bg-gray-100'}`}
                >
                    Array Algorithms (Sort & Search)
                </button>
                <button 
                    onClick={() => setCurrentView('bst')}
                    className={`px-6 py-3 font-bold rounded-lg transition-all ${currentView === 'bst' ? 'bg-blue-600 text-white shadow-md' : 'text-gray-600 hover:bg-gray-100'}`}
                >
                    Binary Search Tree (BST)
                </button>
            </div>

            {/* Content Based on View */}
            {currentView === 'sort' ? renderAlgorithmVisualizer() : <BSTVisualizer />}

        </div>
    </div>
  );
}
