import React from 'react';

const colorMap = {
    default: 'bg-blue-600',
    active: 'bg-yellow-500 animate-pulse', // Currently being visited/compared
    success: 'bg-green-600', // Node was just inserted or replaced
    // Error state could be added later
};

const BSTNode = ({ x, y, value, radius, style = 'default' }) => {
    // Style definition for SVG circle and text
    const fill = {
        default: '#3B82F6', // Blue-600
        active: '#F59E0B',  // Yellow-500
        success: '#10B981', // Green-500
    }[style];

    const stroke = {
        default: '#1E40AF',
        active: '#D97706',
        success: '#047857',
    }[style];

    const animation = style === 'active' ? 'animate-pulse' : '';

    return (
        <g className={`transition-all duration-500 ease-in-out ${animation}`} style={{ transform: `translate(${x}px, ${y}px)` }}>
            {/* The actual node circle */}
            <circle 
                cx="0" 
                cy="0" 
                r={radius} 
                fill={fill}
                stroke={stroke}
                strokeWidth="2"
            />
            {/* The node value text */}
            <text 
                x="0" 
                y="0" 
                textAnchor="middle" 
                dominantBaseline="central" 
                fill="#ffffff" 
                className="font-bold text-sm select-none"
            >
                {value}
            </text>
        </g>
    );
};

export default BSTNode;
