// frontend/src/App.js (Full Visualization Logic)

import React, { useState, useEffect } from 'react';
import ArrayBar from './components/ArrayBar'; 

// Array data the backend uses to generate steps
const INITIAL_ARRAY = [10, 4, 15, 2, 7, 18, 1, 9]; 
const ANIMATION_SPEED_MS = 100; // Time delay between each sorting step

function App() {
  const [steps, setSteps] = useState(null);
  const [currentStepIndex, setCurrentStepIndex] = useState(0);
  const [loadingError, setLoadingError] = useState(null);

  // --- Derived State for Rendering ---
  const currentStep = steps ? steps[currentStepIndex] : null;
  const currentArray = currentStep ? currentStep.array : INITIAL_ARRAY;
  const highlightIndices = currentStep ? currentStep.highlight_indices : [];
  const isSortingComplete = steps && currentStepIndex >= steps.length - 1;

  // --- 1. Fetch Data on Load ---
  useEffect(() => {
    const testData = {
      algorithm: "bubblesort", 
      array: INITIAL_ARRAY 
    };

    // Fetch data using the proxy route
    fetch('/api/visualize', { 
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(testData),
    })
    .then(response => {
        if (!response.ok) {
            throw new Error(`HTTP error! status: ${response.status}`);
        }
        return response.json();
    })
    .then(data => {
      console.log("SUCCESS! Data received from Flask:", data);
      setSteps(data);
      setLoadingError(null);
    })
    .catch(error => {
        console.error('API Communication Failed:', error);
        setLoadingError(`Failed to fetch data: ${error.message}. Check Flask server logs.`);
    });
  }, []);

  // --- 2. Animation Timer Logic ---
  useEffect(() => {
    // Only run if steps are loaded AND we haven't reached the end
    if (!steps || currentStepIndex >= steps.length - 1) return;

    const intervalId = setInterval(() => {
      // Advance to the next visualization step
      setCurrentStepIndex(prevIndex => prevIndex + 1);
    }, ANIMATION_SPEED_MS); 

    // IMPORTANT: Cleanup function to clear the interval when component unmounts
    return () => clearInterval(intervalId);

  }, [steps, currentStepIndex]);
  // --- END Animation Logic ---

  return (
    <div style={{ padding: '20px' }}>
      <h1>Sorting Visualizer</h1>

      {loadingError && <p style={{color: 'red'}}>Error: {loadingError}</p>}

      {/* Container for the bars */}
      <div 
        style={{ 
          display: 'flex', 
          justifyContent: 'center', 
          alignItems: 'flex-end', 
          height: '400px',
          width: '100%',
          borderBottom: '2px solid #ccc'
        }}>

        {currentArray.map((value, index) => (
          <ArrayBar 
            key={index}
            value={value}
            // Highlight bars that were marked for comparison/swap in the current step
            isHighlighted={highlightIndices.includes(index)}
          />
        ))}
      </div>

      {/* Status Bar */}
      <div style={{ marginTop: '20px' }}>
          {!steps && <p>Loading steps...</p>}
          {steps && isSortingComplete && 
              <h2 style={{color: '#4CAF50'}}>Sorting Complete! (Total Steps: {steps.length})</h2>}
          {steps && !isSortingComplete && currentStep &&
              <p>Step {currentStepIndex + 1} of {steps.length}. Action: **{currentStep.action}**</p>}
      </div>
    </div>
  );
}

export default App;
