// ArrayBar.js
import React from 'react';
import './ArrayBar.css'; // You'll create this CSS file

const ArrayBar = ({ value, isHighlighted }) => {
  // Use the 'value' to set the bar's height
  const height = `${value * 3}px`; // Multiply by 3 for better visual scale

  // Use the 'isHighlighted' prop to change the color
  const backgroundColor = isHighlighted ? '#ff4d4d' : '#4d88ff';

  return (
    <div
      className="array-bar"
      style={{
        height: height,
        backgroundColor: backgroundColor,
      }}
    >
      {/* Optional: Display the number value on the bar */}
      <span className="bar-value">{value}</span>
    </div>
  );
};

export default ArrayBar;
