// frontend/src/components/BSTVisualizer.js

import React, { useState, useEffect } from 'react';

// Constants for visualization scaling
const NODE_RADIUS = 20; // Size of the circle nodes
const X_SPACING = 50; // Horizontal distance between nodes (based on calculated X)
const Y_SPACING = 80; // Vertical distance between tree levels (based on calculated Y)
const MARGIN = 30;

// Base API endpoint
const BST_API_URL = '/api/bst';

function BSTVisualizer() {
    const [treeData, setTreeData] = useState({ nodes: [], width: 0, height: 0 });
    const [inputValue, setInputValue] = useState('');
    const [error, setError] = useState(null);

    // --- API Interaction Logic ---
    const fetchTreeStructure = async () => {
        try {
            const response = await fetch(BST_API_URL, {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ action: 'get_tree' }) // Simple GET action
            });
            const data = await response.json();
            if (response.ok) {
                setTreeData(data);
                setError(null);
            } else {
                setError(data.error || 'Failed to fetch tree structure.');
            }
        } catch (e) {
            setError('API connection error. Is the Flask server running?');
        }
    };

    useEffect(() => {
        // Fetch initial tree structure when component mounts
        fetchTreeStructure();
        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, []);

    const handleAction = async (action) => {
        const value = parseInt(inputValue);
        if ((action === 'insert' || action === 'delete') && (isNaN(value) || value < 0)) {
            setError('Please enter a valid non-negative integer.');
            return;
        }

        try {
            const response = await fetch(BST_API_URL, {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ action, value })
            });

            const data = await response.json();

            if (response.ok) {
                setTreeData(data);
                setError(null);
            } else {
                setError(data.error || `Action failed: ${action}`);
            }
        } catch (e) {
            setError('API connection error during action.');
        } finally {
            setInputValue('');
        }
    };

    // --- Visualization Logic ---
    const getScreenCoords = (node) => ({
        // Use the calculated x (in-order position) and spacing
        cx: MARGIN + node.x * X_SPACING,
        // Use the calculated y (depth) and spacing
        cy: MARGIN + node.y * Y_SPACING,
    });

    // Total SVG dimensions based on tree size
    const svgWidth = treeData.width * X_SPACING + 2 * MARGIN;
    const svgHeight = treeData.height * Y_SPACING + 2 * MARGIN + NODE_RADIUS;

    return (
        <div style={{ padding: '20px' }}>
            <h1>Binary Search Tree Visualizer</h1>

            {/* Controls */}
            <div style={{ marginBottom: '20px', display: 'flex', gap: '10px', alignItems: 'center' }}>
                <input 
                    type="number"
                    placeholder="Enter node value"
                    value={inputValue}
                    onChange={(e) => setInputValue(e.target.value)}
                    style={{ padding: '8px' }}
                />
                <button onClick={() => handleAction('insert')} style={{ padding: '8px 15px', backgroundColor: '#4CAF50', color: 'white', border: 'none' }}>
                    Insert
                </button>
                <button onClick={() => handleAction('delete')} style={{ padding: '8px 15px', backgroundColor: '#f44336', color: 'white', border: 'none' }}>
                    Delete
                </button>
                <button onClick={() => handleAction('clear')} style={{ padding: '8px 15px', backgroundColor: '#607d8b', color: 'white', border: 'none' }}>
                    Clear Tree
                </button>
            </div>

            {error && <p style={{ color: 'red' }}>Error: {error}</p>}

            {/* Visualization Area (SVG) */}
            <div style={{ border: '1px solid #ccc', overflowX: 'auto', overflowY: 'hidden' }}>
                <svg width={svgWidth} height={svgHeight}>

                    {/* 1. Draw connecting lines (Edges) */}
                    {treeData.nodes.map(node => {
                        const parentCoords = getScreenCoords(node);

                        // Find child nodes to draw lines
                        const leftChild = treeData.nodes.find(n => n.key === node.left_key);
                        const rightChild = treeData.nodes.find(n => n.key === node.right_key);

                        const lines = [];

                        // Draw line to Left Child
                        if (leftChild) {
                            const childCoords = getScreenCoords(leftChild);
                            lines.push(
                                <line 
                                    key={`line-l-${node.key}`}
                                    x1={parentCoords.cx} y1={parentCoords.cy} 
                                    x2={childCoords.cx} y2={childCoords.cy} 
                                    stroke="grey" 
                                    strokeWidth="1"
                                />
                            );
                        }

                        // Draw line to Right Child
                        if (rightChild) {
                            const childCoords = getScreenCoords(rightChild);
                            lines.push(
                                <line 
                                    key={`line-r-${node.key}`}
                                    x1={parentCoords.cx} y1={parentCoords.cy} 
                                    x2={childCoords.cx} y2={childCoords.cy} 
                                    stroke="grey" 
                                    strokeWidth="1"
                                />
                            );
                        }

                        return lines;
                    })}

                    {/* 2. Draw nodes (Vertices) on top of lines */}
                    {treeData.nodes.map(node => {
                        const { cx, cy } = getScreenCoords(node);

                        return (
                            <g key={node.key}>
                                {/* Node Circle */}
                                <circle 
                                    cx={cx} 
                                    cy={cy} 
                                    r={NODE_RADIUS} 
                                    fill="#4A90E2" 
                                    stroke="#333" 
                                    strokeWidth="1"
                                />
                                {/* Node Text (Key) */}
                                <text 
                                    x={cx} 
                                    y={cy + 5} 
                                    textAnchor="middle" 
                                    fill="white" 
                                    fontSize="12">
                                    {node.key}
                                </text>
                            </g>
                        );
                    })}
                </svg>
            </div>
        </div>
    );
}

export default BSTVisualizer;
