// frontend/src/App.js (Example fetch call)
import React, { useState, useEffect } from 'react';

function App() {
  const [visualizationSteps, setVisualizationSteps] = useState(null);

  // Test data to send to Flask
  const testData = {
    algorithm: "bubblesort", 
    array: [10, 4, 15, 2] // The input array the backend will sort
  };

  useEffect(() => {
    // Note: We use the proxy set in package.json, so we just use /api/visualize
    fetch('/api/visualize', { 
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(testData),
    })
    .then(response => response.json())
    .then(data => {
      console.log("Data received from Flask:", data);
      setVisualizationSteps(data); // Store the steps for animation
    })
    .catch(error => console.error('Error fetching data:', error));
  }, []);

  return (
    <div className="App">
      <h1>Algorithm Visualizer</h1>
      {visualizationSteps ? (
        <p>Successfully received {visualizationSteps.length} steps from Flask!</p>
      ) : (
        <p>Loading steps...</p>
      )}
    </div>
  );
}

export default App;
