    }
  }
  const finalSortedIndices = Array.from({
    length: n
  }, (_, i) => i);
  steps.push({
    array: [...array],
    highlight_indices: [],
    action: "Finished Bubble Sort",
    sorted_indices: finalSortedIndices
  });
  return steps;
};

