import React, { useState, useEffect, useCallback, useRef } from 'react'; // Added useRef for animation loop

// --- Components (Moved locally for single-file mandate) ---

// 1. ArrayBar Component
const ArrayBar = React.memo(({ value, height, width, isHighlighted, isPivot, isSorted, isSearchMid, isSearchRange, isFound, isLinearCheck }) => {
    // Green: Sorted, Blue: Default, Orange: Highlighted (Comparison/Swap), Violet: Pivot/MinIndex
    let color = '#007bff'; // Default Blue

    // --- Search Mode Logic ---
    let opacity = '1';

    // Binary Search Specific Colors
    if (isSearchMid || isSearchRange || isFound) {
        // Elements in the current search range
        color = '#3b82f6'; // Light Blue
        opacity = '1';

        if (isSearchMid) {
            // Mid element
            color = '#f59e0b'; // Amber/Mid
        }

        if (isFound) {
            // Found element
            color = '#10b981'; // Emerald/Found
        }
    } else if (isSearchMid === false && isSearchRange === false && value) { 
        // Excluded elements during Binary Search
        opacity = '0.3';
        color = '#9ca3af'; // Gray
    }

    // Linear Search Specific Color
    if (isLinearCheck) {
        color = '#FF5733'; // Orange for comparison/swap
        if (isFound) {
            color = '#10b981'; // Emerald/Found
        }
    }


    // --- Sorting Mode Logic (Applied if not in active search highlight) ---
    if (isSorted) {
        color = '#4CAF50';
        opacity = '1';
    } else if (isPivot) {
        color = '#8A2BE2'; // BlueViolet for Pivot/MinIndex
    } else if (isHighlighted && !isSearchRange && !isSearchMid && !isLinearCheck) {
        color = '#FF5733'; // Orange for comparison/swap
    }

    const finalWidth = width > 0 ? `${width}px` : 'auto';

    return (
        <div 
            style={{
                height: `${height}px`,
                width: finalWidth,
                margin: '0 1px',
                backgroundColor: color,
                transition: 'height 0.1s linear, background-color 0.1s linear, opacity 0.1s linear',
                borderRadius: '2px 2px 0 0',
                display: 'flex',
                alignItems: 'flex-end',
                justifyContent: 'center',
                fontSize: '10px',
                color: 'white',
                fontWeight: 'bold',
                position: 'relative',
                opacity: opacity,
            }}
            title={`Value: ${value}`}
        >
            {/* Show value for smaller arrays */}
            {width > 20 && value}
        </div>
    );
});

// 2. BST Visualizer Component (Simplified/Dummy) - (Omitted for brevity, kept structure)
const BSTVisualizer = () => {
    // ... (Existing BST Visualizer Component code remains unchanged)
    const [treeData, setTreeData] = useState([]);
    const [inputValue, setInputValue] = useState('');
    const [message, setMessage] = useState('Enter a number to build the BST.');

    const insertNode = useCallback((data, value) => {
        if (!data) return { value: value, left: null, right: null };
        if (value < data.value) {
            data.left = insertNode(data.left, value);
        } else if (value > data.value) {
            data.right = insertNode(data.right, value);
        }
        return data;
    }, []);

    const handleInsert = () => {
        const num = parseInt(inputValue);
        if (isNaN(num)) {
            setMessage('Please enter a valid number.');
            return;
        }

        let newTreeData = [...treeData];
        if (newTreeData.length === 0) {
            newTreeData.push({ value: num, left: null, right: null });
        } else {
            newTreeData = [insertNode(newTreeData[0], num)];
        }

        setTreeData(newTreeData);
        setInputValue('');
        setMessage(`Inserted ${num}. Try inserting another number.`);
    };

    const renderTree = (node) => {
        if (!node) return null;
        return (
            <div style={{ margin: '10px', padding: '10px', border: '1px solid #ccc', borderRadius: '8px', display: 'inline-block', backgroundColor: '#e9f7ff' }}>
                <p style={{ fontWeight: 'bold', margin: 0 }}>Root: {node.value}</p>
                <div style={{ display: 'flex', justifyContent: 'center', marginTop: '10px' }}>
                    {node.left && <div style={{ marginRight: '10px', borderRight: '1px dashed #ccc' }}>L: {renderTree(node.left)}</div>}
                    {node.right && <div style={{ marginLeft: '10px', borderLeft: '1px dashed #ccc' }}>R: {renderTree(node.right)}</div>}
                </div>
            </div>
        );
    };

    return (
        <div>
            <h1>Binary Search Tree Visualizer</h1>
            <p className="text-sm text-gray-600">{message}</p>
            <div style={{ display: 'flex', gap: '10px', marginBottom: '20px' }}>
                <input
                    type="number"
                    value={inputValue}
                    onChange={(e) => setInputValue(e.target.value)}
                    placeholder="Enter number"
                    style={{ padding: '8px', border: '1px solid #ccc', borderRadius: '4px', minWidth: '150px' }}
                />
                <button 
                    onClick={handleInsert} 
                    style={{ padding: '8px 15px', cursor: 'pointer', backgroundColor: '#007bff', color: 'white', border: 'none', borderRadius: '4px' }}
                >
                    Insert Node
                </button>
            </div>

            <div style={{ 
                minHeight: '400px', 
                backgroundColor: '#f9f9f9', 
                padding: '20px', 
                border: '1px solid #eee',
                borderRadius: '8px',
                textAlign: 'center'
            }}>
                {treeData.length > 0 ? (
                    renderTree(treeData[0])
                ) : (
                    <p style={{marginTop: '150px', color: '#888'}}>Tree is empty. Insert the first node!</p>
                )}
            </div>
        </div>
    );
};


// --- Constants ---
const DEFAULT_ARRAY_SIZE = 15;
const MAX_ARRAY_VALUE = 400; 
const MIN_ARRAY_VALUE = 10;
const ANIMATION_SPEED_MS = 80; 

// --- Algorithm Pseudocode ---
const PSEUDOCODE = {
    bubblesort: [
        "function bubbleSort(array):",
        "  n = array.length",
        "  for i from 0 to n - 2:",
        "    for j from 0 to n - 2 - i:",
        "      // Compare & Highlight array[j] and array[j+1]",
        "      if array[j] > array[j+1]:",
        "        // Swap elements",
        "        swap(array[j], array[j+1])",
        "  return array"
    ],
    mergesort: [
        "function mergeSort(array):",
        "  if array.length <= 1: return array",
        "  mid = floor(array.length / 2)",
        "  left = mergeSort(array[0...mid])",
        "  right = mergeSort(array[mid...n])",
        "  return merge(left, right)",
        "",
        "function merge(left, right):",
        "  // Merging sub-arrays and placing back into the original array...",
        "  result = []",
        "  while left & right are not empty:",
        "    if left[0] <= right[0]:",
        "      result.push(left.shift())",
        "    else:",
        "      result.push(right.shift())",
        "  return result + left + right"
    ],
    quicksort: [
        "function quickSort(array, low, high):",
        "  if low < high:",
        "    // Partition the array",
        "    pi = partition(array, low, high)",
        "    // Recursively sort left and right sub-arrays",
        "    quickSort(array, low, pi - 1)",
        "    quickSort(array, pi + 1, high)",
        "",
        "function partition(array, low, high):",
        "  pivot = array[high] // Choose the last element as pivot",
        "  i = low - 1",
        "  for j from low to high - 1:",
        "    if array[j] < pivot:",
        "      i++",
        "      swap(array[i], array[j])",
        "  swap(array[i+1], array[high])",
        "  return i + 1"
    ],
    shellsort: [
        "function shellSort(array):",
        "  n = array.length",
        "  gap = floor(n / 2)",
        "  while gap > 0:",
        "    // Perform gapped insertion sort",
        "    for i from gap to n - 1:",
        "      temp = array[i]",
        "      j = i",
        "      while j >= gap and array[j - gap] > temp:",
        "        // Shift elements (Highlight comparison and shift)",
        "        array[j] = array[j - gap]",
        "        j = j - gap",
        "      // Place temp (Highlight placement)",
        "      array[j] = temp",
        "    gap = floor(gap / 2) // Reduce the gap"
    ],
    selectionsort: [
        "function selectionSort(array):",
        "  n = array.length",
        "  for i from 0 to n - 2:",
        "    // Assume current position i holds the minimum",
        "    minIndex = i",
        "    for j from i + 1 to n - 1:",
        "      // Highlight comparison between array[j] and array[minIndex]",
        "      if array[j] < array[minIndex]:",
        "        // Update the index of the minimum element",
        "        minIndex = j",
        "    // If a smaller element was found, swap it into position i",
        "    if minIndex is not i:",
        "      swap(array[i], array[minIndex])",
        "    // Position i is finalized",
        "  return array"
    ],
    binarysearch: [
        "function binarySearch(array, target):",
        "  // Array MUST be sorted",
        "  low = 0",
        "  high = array.length - 1",
        "  while low <= high:",
        "    mid = floor((low + high) / 2)",
        "    // Highlight mid element (array[mid])",
        "    if array[mid] == target:",
        "      return mid",
        "    else if array[mid] < target:",
        "      // Search right (New Low = mid + 1)",
        "      low = mid + 1",
        "    else:",
        "      // Search left (New High = mid - 1)",
        "      high = mid - 1",
        "  return -1"
    ],
    // ðŸŒŸ ADDED LINEAR SEARCH PSEUDOCODE ðŸŒŸ
    linearsearch: [
        "function linearSearch(array, target):",
        "  n = array.length",
        "  for i from 0 to n - 1:",
        "    // Highlight array[i]",
        "    if array[i] == target:",
        "      return i",
        "  return -1"
    ]
};

// --- Helper Functions ---
const generateRandomArray = (size) => {
    return Array.from({ length: size }, () => 
        Math.floor(Math.random() * (MAX_ARRAY_VALUE - MIN_ARRAY_VALUE + 1)) + MIN_ARRAY_VALUE
    );
};

const parseCustomArray = (inputString) => {
    const elements = inputString.split(',')
        .map(s => {
            const num = parseInt(s.trim());
            return isNaN(num) || num <= 0 ? null : Math.min(num, MAX_ARRAY_VALUE);
        })
        .filter(n => n !== null);
    return elements;
};

// --- CLIENT-SIDE ALGORITHM LOGIC ---

/**
 * Generates steps for Bubble Sort visualization. (Existing logic)
 */
const getBubbleSortSteps = (initialArray) => {
    // ... (Existing Bubble Sort logic remains unchanged)
    const array = [...initialArray];
    const steps = [];
    const n = array.length;
    let sortedIndices = [];

    // ... (Bubble Sort loop and step recording)
     for (let i = 0; i < n - 1; i++) {
        let swapped = false;
        for (let j = 0; j < n - 1 - i; j++) {
            // 1. Highlight comparison
            steps.push({
                array: [...array], 
                highlight_indices: [j, j + 1], 
                action: `Comparing array[${j}] and array[${j + 1}]`,
                sorted_indices: [...sortedIndices]
            });

            if (array[j] > array[j + 1]) {
                // 2. Swap
                [array[j], array[j + 1]] = [array[j + 1], array[j]];
                swapped = true;
                steps.push({
                    array: [...array], 
                    highlight_indices: [j, j + 1], 
                    action: `Swapping ${array[j+1]} and ${array[j]}`, 
                    sorted_indices: [...sortedIndices]
                });
            }
        }

        if (n - 1 - i >= 0) {
            sortedIndices.push(n - 1 - i);
        }

        if (!swapped) {
            for (let k = 0; k < n - 1 - i; k++) {
                if (!sortedIndices.includes(k)) {
                     sortedIndices.push(k);
                }
            }
            break;
        }
    }

    const finalSortedIndices = Array.from({ length: n }, (_, i) => i);
    steps.push({
        array: [...array], 
        highlight_indices: [], 
        action: "Finished Bubble Sort",
        sorted_indices: finalSortedIndices
    });

    return steps;
};

/**
 * Generates steps for Merge Sort visualization. (Existing logic)
 */
const getMergeSortSteps = (initialArray) => {
    // ... (Existing Merge Sort logic remains unchanged)
    const array = [...initialArray];
    const steps = [];

    const merge = (arr, start, mid, end) => {
        const left = arr.slice(start, mid);
        const right = arr.slice(mid, end);
        let i = 0, j = 0, k = start;

        steps.push({
            array: [...array],
            highlight_indices: Array.from({ length: end - start }, (_, idx) => start + idx),
            action: `Merging sub-arrays from index ${start} to ${end - 1}`,
            sorted_indices: []
        });

        while (i < left.length && j < right.length) {
            if (left[i] <= right[j]) {
                array[k] = left[i];
                i++;
            } else {
                array[k] = right[j];
                j++;
            }
            k++;

            steps.push({
                array: [...array],
                highlight_indices: [k - 1], 
                action: `Placing element at index ${k - 1}`,
                sorted_indices: []
            });
        }

        while (i < left.length) {
            array[k] = left[i];
            i++; k++;
            steps.push({
                array: [...array],
                highlight_indices: [k - 1],
                action: `Placing remaining element from left half at ${k - 1}`,
                sorted_indices: []
            });
        }
        while (j < right.length) {
            array[k] = right[j];
            j++; k++;
            steps.push({
                array: [...array],
                highlight_indices: [k - 1],
                action: `Placing remaining element from right half at ${k - 1}`,
                sorted_indices: []
            });
        }

        steps.push({
            array: [...array],
            highlight_indices: [],
            action: `Merged segment complete from index ${start} to ${end - 1}`,
            sorted_indices: [] 
        });
    };

    const mergeSortHelper = (arr, start, end) => {
        if (end - start <= 1) return;

        const mid = Math.floor((start + end) / 2);
        mergeSortHelper(arr, start, mid);
        mergeSortHelper(arr, mid, end);
        merge(arr, start, mid, end);
    };

    mergeSortHelper(array, 0, array.length);

    const finalSortedIndices = Array.from({ length: array.length }, (_, i) => i);
    steps.push({
        array: [...array], 
        highlight_indices: [], 
        action: "Finished Merge Sort",
        sorted_indices: finalSortedIndices
    });

    return steps;
};


/**
 * Generates steps for Quick Sort visualization (Lomuto Partition Scheme). (Existing logic)
 */
const getQuickSortSteps = (initialArray) => {
    // ... (Existing Quick Sort logic remains unchanged)
    const array = [...initialArray];
    const steps = [];

    const recordStep = (action, highlight_indices = [], pivot_index = null, sorted_indices = []) => {
        steps.push({
            array: [...array],
            action: action,
            highlight_indices: highlight_indices,
            pivot_index: pivot_index,
            sorted_indices: [...sorted_indices]
        });
    };

    const partition = (arr, low, high, sortedIndices) => {
        const pivot = arr[high]; 
        let i = low - 1; 

        recordStep(`Selecting pivot ${pivot} at index ${high}`, [], high, sortedIndices);

        for (let j = low; j <= high - 1; j++) {
            recordStep(`Comparing ${arr[j]} at ${j} with pivot ${pivot}`, [j], high, sortedIndices);

            if (arr[j] < pivot) {
                i++;
                [arr[i], arr[j]] = [arr[j], arr[i]];
                recordStep(`Swapping ${arr[i]} at ${i} and ${arr[j]} at ${j}`, [i, j], high, sortedIndices);
            }
        }

        const pivotIndex = i + 1;
        [arr[pivotIndex], arr[high]] = [arr[high], arr[pivotIndex]];

        recordStep(`Pivot ${pivot} placed correctly at index ${pivotIndex}`, [pivotIndex], null, [...sortedIndices, pivotIndex]);

        return pivotIndex;
    };

    const sortedIndicesTracker = [];

    const quickSortHelper = (arr, low, high) => {
        if (low < high) {
            const pi = partition(arr, low, high, sortedIndicesTracker);

            if (!sortedIndicesTracker.includes(pi)) {
                sortedIndicesTracker.push(pi);
            }

            quickSortHelper(arr, low, pi - 1);
            quickSortHelper(arr, pi + 1, high);
        } else if (low === high && low >= 0) {
            if (!sortedIndicesTracker.includes(low)) {
                sortedIndicesTracker.push(low);
                recordStep(`Sub-array of size 1 at index ${low} is sorted.`, [], null, sortedIndicesTracker);
            }
        }
    };

    quickSortHelper(array, 0, array.length - 1);

    const finalSortedIndices = Array.from({ length: array.length }, (_, i) => i);
    steps.push({
        array: [...array], 
        action: "Finished Quick Sort",
        highlight_indices: [],
        pivot_index: null,
        sorted_indices: finalSortedIndices
    });

    return steps;
};

/**
 * Generates steps for Shell Sort visualization (using array halving gap). (Existing logic)
 */
const getShellSortSteps = (initialArray) => {
    // ... (Existing Shell Sort logic remains unchanged)
    const array = [...initialArray];
    const steps = [];
    const n = array.length;
    let gap = Math.floor(n / 2);
    let sortedIndices = [];

    const recordStep = (action, highlight_indices = [], current_gap = gap, sorted_indices = []) => {
        steps.push({
            array: [...array],
            action: action,
            highlight_indices: highlight_indices,
            gap: current_gap,
            sorted_indices: [...sorted_indices]
        });
    };

    while (gap > 0) {
        recordStep(`Starting pass with Gap = ${gap}`, [], gap, sortedIndices);

        for (let i = gap; i < n; i++) {
            const temp = array[i];
            let j = i;

            recordStep(`Selecting element ${temp} at index ${i}. Gap = ${gap}`, [i], gap, sortedIndices);

            while (j >= gap && array[j - gap] > temp) {
                recordStep(`Comparing ${array[j - gap]} at ${j - gap} and ${temp} (to be placed at ${j}). Gap = ${gap}`, [j, j - gap], gap, sortedIndices);

                array[j] = array[j - gap];

                recordStep(`Shifting ${array[j - gap]} to ${j}. Gap = ${gap}`, [j], gap, sortedIndices);

                j -= gap;
            }

            array[j] = temp;

            recordStep(`Placing ${temp} at index ${j}. Gap = ${gap}`, [j], gap, sortedIndices);
        }

        gap = Math.floor(gap / 2);
    }

    const finalSortedIndices = Array.from({ length: n }, (_, i) => i);
    recordStep("Finished Shell Sort", [], 0, finalSortedIndices);

    return steps;
};

/**
 * Generates steps for Selection Sort visualization. (Existing logic)
 */
const getSelectionSortSteps = (initialArray) => {
    // ... (Existing Selection Sort logic remains unchanged)
    const array = [...initialArray];
    const steps = [];
    const n = array.length;
    let sortedIndices = [];

    const recordStep = (action, highlight_indices = [], min_index = null, sorted_indices = []) => {
        steps.push({
            array: [...array],
            action: action,
            highlight_indices: highlight_indices,
            pivot_index: min_index, 
            sorted_indices: [...sorted_indices]
        });
    };

    for (let i = 0; i < n - 1; i++) {
        let minIndex = i;

        recordStep(`Starting pass ${i + 1}. Assume minimum is ${array[i]} at index ${i}.`, [], minIndex, sortedIndices);

        for (let j = i + 1; j < n; j++) {
            recordStep(`Comparing ${array[j]} at ${j} with current minimum ${array[minIndex]} at ${minIndex}.`, [j], minIndex, sortedIndices);

            if (array[j] < array[minIndex]) {
                minIndex = j;
                recordStep(`New minimum found: ${array[minIndex]} at index ${minIndex}.`, [j], minIndex, sortedIndices);
            }
        }

        if (minIndex !== i) {
            [array[i], array[minIndex]] = [array[minIndex], array[i]];
            recordStep(`Swapping ${array[i]} (new smallest) with ${array[minIndex]} (current position) to place smallest element at ${i}.`, [i, minIndex], null, sortedIndices);
        } else {
             recordStep(`Element at ${i} is already the smallest in the unsorted portion.`, [], null, sortedIndices);
        }

        sortedIndices.push(i);
        recordStep(`Position ${i} is finalized (Sorted).`, [], null, sortedIndices);
    }

    if (n > 0 && !sortedIndices.includes(n - 1)) {
        sortedIndices.push(n - 1);
    }

    const finalSortedIndices = Array.from({ length: n }, (_, k) => k);
    recordStep("Finished Selection Sort", [], null, finalSortedIndices);

    return steps;
};

/**
 * Generates steps for Binary Search visualization. (Existing logic)
 */
const getBinarySearchSteps = (arr, target) => {
    // ... (Existing Binary Search logic remains unchanged)
    const steps = [];
    const array = [...arr];

    steps.push({
        array: [...array],
        action: `Search started for target ${target}. Array must be sorted.`,
        low: 0,
        high: array.length - 1,
        mid: -1,
        found: false,
        search_target: target,
        indices: [], 
    });

    let low = 0;
    let high = array.length - 1;
    let found = false;
    let mid = -1;

    while (low <= high) {
        mid = Math.floor((low + high) / 2);

        steps.push({
            array: [...array],
            action: `Checking mid element at index ${mid}: Value is ${array[mid]}`,
            low: low,
            high: high,
            mid: mid,
            found: false,
            search_target: target,
            highlight_indices: [mid],
        });

        if (array[mid] === target) {
            found = true;
            break;
        } else if (array[mid] < target) {
            low = mid + 1;
            steps.push({
                array: [...array],
                action: `Target (${target}) is greater than ${array[mid]}. Searching right half (New Low: ${low}).`,
                low: low,
                high: high,
                mid: mid,
                found: false,
                search_target: target,
                highlight_indices: [],
            });
        } else {
            high = mid - 1;
            steps.push({
                array: [...array],
                action: `Target (${target}) is less than ${array[mid]}. Searching left half (New High: ${high}).`,
                low: low,
                high: high,
                mid: mid,
                found: false,
                search_target: target,
                highlight_indices: [],
            });
        }
    }

    if (found) {
        steps.push({
            array: [...array],
            action: `SUCCESS! Target ${target} found at index ${mid}. Search complete.`,
            low: low,
            high: high,
            mid: mid,
            found: true,
            search_target: target,
            highlight_indices: [mid],
        });
    } else {
        steps.push({
            array: [...array],
            action: `FAILURE! Target ${target} not found. Low (${low}) > High (${high}).`,
            low: low,
            high: high,
            mid: -1,
            found: false,
            search_target: target,
            highlight_indices: [],
        });
    }

    return steps;
};

// ðŸŒŸ ADDED LINEAR SEARCH FUNCTION ðŸŒŸ
/**
 * Generates steps for Linear Search visualization. (Based on user's Python logic)
 */
const getLinearSearchSteps = (arr, target) => {
    const steps = [];
    const array = [...arr];
    const n = array.length;
    let found = false;
    let foundIndex = -1;

    // Initial state tracking
    steps.push({
        array: [...array],
        action: `Linear Search started for target ${target}.`,
        current_index: -1,
        found: false,
        search_target: target,
        highlight_indices: [],
    });

    for (let i = 0; i < n; i++) {

        // Track comparison
        steps.push({
            array: [...array],
            action: `Comparing element at index ${i}: Value is ${array[i]}`,
            current_index: i, // Index currently being checked
            found: false,
            search_target: target,
            highlight_indices: [i], // Highlight the current index
        });

        if (array[i] === target) {
            found = true;
            foundIndex = i;
            break;
        }

        // Track comparison failure (if not found)
        steps.push({
            array: [...array],
            action: `Value ${array[i]} does not match ${target}. Moving to next index.`,
            current_index: i,
            found: false,
            search_target: target,
            highlight_indices: [], 
        });
    }

    // Final result tracking
    if (found) {
        steps.push({
            array: [...array],
            action: `SUCCESS! Target ${target} found at index ${foundIndex}. Search complete.`,
            current_index: foundIndex,
            found: true,
            search_target: target,
            highlight_indices: [foundIndex],
        });
    } else {
        steps.push({
            array: [...array],
            action: `FAILURE! Target ${target} not found after checking all elements. Search complete.`,
            current_index: n - 1,
            found: false,
            search_target: target,
            highlight_indices: [],
        });
    }

    return steps;
};


function App() {
  const [currentView, setCurrentView] = useState('sort'); 

  // Sorting Visualizer States
  const [steps, setSteps] = useState(null);
  const [currentStepIndex, setCurrentStepIndex] = useState(0);
  const [selectedAlgorithm, setSelectedAlgorithm] = useState("linearsearch"); // Changed default to new algorithm
  const [loadingError, setLoadingError] = useState(null);
  const [inputArray, setInputArray] = useState(() => generateRandomArray(DEFAULT_ARRAY_SIZE));
  const [arraySize, setArraySize] = useState(DEFAULT_ARRAY_SIZE);
  const [customInputData, setCustomInputData] = useState("");
  const [speed, setSpeed] = useState(ANIMATION_SPEED_MS);

  // NEW STATE FOR SEARCH ALGORITHMS
  const [searchTarget, setSearchTarget] = useState(50); 

  // Animation State
  const [isAnimating, setIsAnimating] = useState(false);
  const animationRef = useRef(null);

  // Derived States
  const currentStep = steps ? steps[currentStepIndex] : null;
  const currentArray = currentStep ? currentStep.array : inputArray; 
  const highlightIndices = currentStep ? currentStep.highlight_indices : [];
  const pivotIndex = currentStep ? currentStep.pivot_index : null;
  const sortedIndices = currentStep ? currentStep.sorted_indices || [] : [];
  const isSortingComplete = steps && currentStepIndex >= steps.length - 1; // Simplified completion check
  const currentPseudocode = PSEUDOCODE[selectedAlgorithm];

  const isSearchAlgorithm = selectedAlgorithm === 'binarysearch' || selectedAlgorithm === 'linearsearch';
  const isSupportedAlgorithm = ["bubblesort", "mergesort", "quicksort", "shellsort", "selectionsort", "binarysearch", "linearsearch"].includes(selectedAlgorithm); // ADDED linearsearch

  // --- Handlers ---
  const handleGenerateNewArray = (size = arraySize) => {
      const newArray = generateRandomArray(size);
      setInputArray(newArray);
      setCustomInputData(""); 
      setArraySize(size);
      // Ensure the search steps are regenerated immediately with the new array
      if (isSearchAlgorithm) {
          // This relies on the main useEffect for regeneration
          setSearchTarget(newArray[Math.floor(newArray.length / 2)] || 50); 
      }
  };

  const handleLoadCustomArray = () => {
      const newArray = parseCustomArray(customInputData);
      if (newArray.length > 0) {
          setInputArray(newArray);
          setArraySize(newArray.length); 
          setLoadingError(null);
          // Set a default search target from the new array
          if (isSearchAlgorithm) {
            setSearchTarget(newArray[Math.floor(newArray.length / 2)] || 50);
          }
      } else {
          setLoadingError("Invalid or empty custom array input. Please use comma-separated integers (e.g., 5,12,3,40).");
      }
  };

  // Function to re-run the search on button click
  const handleSearchClick = () => {
    // This simply triggers the main useEffect by updating the search target
    // We update it to its current value to force a step regeneration,
    // in case the array or algorithm was changed without the target changing.
    setSearchTarget(prev => {
        // We use a dummy change to force useEffect if the array changed but the target didn't
        return prev === searchTarget ? searchTarget + 0.0001 : searchTarget;
    });
    // The useEffect below handles the step generation
  };


  // --- 1. Generate Data on Array/Algorithm/Target Change (Sorting/Search only) ---
  useEffect(() => {
    if (currentView !== 'sort' || inputArray.length === 0) return;

    // Clear animation loop
    if (animationRef.current) {
        clearTimeout(animationRef.current);
        setIsAnimating(false);
    }

    // Determine if the selected algorithm is a search algorithm
    const isSearchAlgo = selectedAlgorithm === "binarysearch" || selectedAlgorithm === "linearsearch"; 

    // Clear steps/error before generation
    setSteps(null);
    setCurrentStepIndex(0);
    setLoadingError(null);

    if (!isSupportedAlgorithm) { 
        setLoadingError(`Algorithm ${selectedAlgorithm} is unsupported.`);
        return; 
    }

    let processedArray = inputArray.map(val => Number(val));

    // Binary Search REQUIRES a sorted array. Linear Search does not.
    if (selectedAlgorithm === "binarysearch") {
        // Sort a copy for binary search
        processedArray.sort((a, b) => a - b);
        // And update the displayed array to the sorted one
        setInputArray(processedArray); 
    } else if (selectedAlgorithm === "linearsearch") {
        // Linear search works on the original array, but we make a copy for steps
        // The display array (inputArray) remains the potentially unsorted one.
    }

    let newSteps = [];

    try {
        if (selectedAlgorithm === "bubblesort") {
            newSteps = getBubbleSortSteps(processedArray);
        } else if (selectedAlgorithm === "mergesort") {
            newSteps = getMergeSortSteps(processedArray);
        } else if (selectedAlgorithm === "quicksort") { 
            newSteps = getQuickSortSteps(processedArray);
        } else if (selectedAlgorithm === "shellsort") { 
            newSteps = getShellSortSteps(processedArray);
        } else if (selectedAlgorithm === "selectionsort") { 
             newSteps = getSelectionSortSteps(processedArray);
        } else if (isSearchAlgo) { 
             const target = isNaN(Number(searchTarget)) ? 0 : Number(searchTarget);

             if (selectedAlgorithm === "binarysearch") {
                 newSteps = getBinarySearchSteps(processedArray, target);
             } else if (selectedAlgorithm === "linearsearch") {
                 // Use the current inputArray (potentially unsorted) for linear search steps
                 newSteps = getLinearSearchSteps(inputArray.map(val => Number(val)), target);
             }
        }

        if (newSteps && newSteps.length > 0) {
            setSteps(newSteps);
            setCurrentStepIndex(0); // Reset step index
        } else {
             setLoadingError(`Algorithm ${selectedAlgorithm} failed to generate steps or resulted in an empty step list.`);
        }

    } catch (error) {
        console.error('Algorithm Generation Failed:', error);
        setLoadingError(`Algorithm generation failed for ${selectedAlgorithm}: ${error.message}`);
    }

  }, [selectedAlgorithm, inputArray, isSupportedAlgorithm, currentView, searchTarget]); 
  // Dependency includes searchTarget to automatically re-run search on target change


  // --- 2. Animation Timer Logic (Sorting/Search/BST) ---
  useEffect(() => {
    if (currentView !== 'sort' || !steps || currentStepIndex >= steps.length - 1 || !isAnimating) return;

    const timeout = setTimeout(() => {
      setCurrentStepIndex(prev => prev + 1);
    }, speed);

    animationRef.current = timeout;

    return () => clearTimeout(timeout);
  }, [currentStepIndex, steps, isAnimating, speed, currentView]);

  // --- Utility for the Visualizer ---
  const maxArrayValue = Math.max(...inputArray, 1);
  const maxBarHeight = 350; 
  const barWidth = 400 / inputArray.length; // Adjusted for better visual width calculation

  // --- Render Functions ---

  // Component for Sorting/Searching
  const ArrayVisualizer = () => {

      const currentLow = currentStep?.low ?? -1;
      const currentHigh = currentStep?.high ?? -1;
      const currentMid = currentStep?.mid ?? -1;
      const isFound = currentStep?.found ?? false;
      const currentLinearIndex = currentStep?.current_index ?? -1;

      // Determine bar width in pixels
      const arrayBarWidthPx = Math.max(10, Math.floor(600 / currentArray.length) - 2);


      return (
        <div className="bg-white p-6 rounded-xl shadow-2xl">

            <h2 className="text-3xl font-bold text-gray-800 mb-6">
                {isSearchAlgorithm ? 'Search Visualizer' : 'Sorting Visualizer'}
            </h2>

            {/* Algorithm Selection and Input */}
            <div className="flex flex-wrap items-center space-x-4 mb-6 p-4 border rounded-xl bg-gray-50">
                <div className="flex items-center space-x-2">
                    <label htmlFor="algorithm-select" className="font-semibold text-gray-700">Algorithm:</label>
                    <select
                        id="algorithm-select"
                        className="p-2 border border-gray-300 rounded-lg shadow-sm"
                        value={selectedAlgorithm}
                        onChange={(e) => setSelectedAlgorithm(e.target.value)}
                        disabled={isAnimating}
                    >
                        {/* Sorting Algorithms */}
                        <option value="selectionsort">Selection Sort (O(nÂ²))</option>
                        <option value="bubblesort">Bubble Sort (O(nÂ²))</option>
                        <option value="quicksort">Quick Sort (O(n log n))</option>
                        <option value="mergesort">Merge Sort (O(n log n))</option>
                        <option value="shellsort">Shell Sort (O(n logÂ² n))</option>
                        <option disabled>--- Search ---</option>
                        {/* Search Algorithms */}
                        <option value="linearsearch">Linear Search (O(n))</option> {/* ðŸŒŸ ADDED LINEAR SEARCH ðŸŒŸ */}
                        <option value="binarysearch">Binary Search (O(log n))</option>
                    </select>
                </div>

                {/* Search Input */}
                {isSearchAlgorithm && (
                    <div className="flex items-center space-x-2 ml-4">
                        <input
                            type="number"
                            placeholder="Target Value"
                            className="p-2 border border-gray-300 rounded-lg w-32 focus:ring-green-500 focus:border-green-500 transition-shadow"
                            value={searchTarget}
                            onChange={(e) => setSearchTarget(Number(e.target.value))}
                            onKeyDown={(e) => {
                                if (e.key === 'Enter') handleSearchClick();
                            }}
                            disabled={isAnimating}
                        />
                        <button
                            className="px-4 py-2 bg-green-500 text-white font-semibold rounded-lg hover:bg-green-600 transition-colors shadow-md disabled:bg-gray-400"
                            onClick={handleSearchClick}
                            disabled={isAnimating || isNaN(searchTarget)}
                        >
                            Start Search
                        </button>
                         {selectedAlgorithm === 'binarysearch' && 
                             <span className="text-sm text-red-500 font-semibold">(Array is auto-sorted for BS)</span>
                         }
                    </div>
                )}
            </div>

            {/* Array Controls (Always visible for array manipulation) */}
            <div className="flex flex-wrap items-center space-x-4 mb-6 p-4 border rounded-xl bg-gray-50">
                 <div className="flex items-center space-x-2">
                    <label htmlFor="array-size" className="font-semibold text-gray-700">Array Size ({arraySize}):</label>
                    <input
                        id="array-size"
                        type="range"
                        min="5"
                        max="50"
                        value={arraySize}
                        onChange={(e) => {
                            const newSize = parseInt(e.target.value, 10);
                            handleGenerateNewArray(newSize);
                        }}
                        className="w-40 h-2 bg-gray-200 rounded-lg appearance-none cursor-pointer range-lg"
                        disabled={isAnimating}
                    />
                </div>

                <button
                    className="px-4 py-2 bg-yellow-500 text-white font-semibold rounded-lg hover:bg-yellow-600 transition-colors shadow-md disabled:bg-gray-400"
                    onClick={() => handleGenerateNewArray()}
                    disabled={isAnimating}
                >
                    Generate Random Array
                </button>

                <div className="flex items-center space-x-2">
                     <input
                        type="text"
                        placeholder="Custom Array (e.g., 5,12,3,40)"
                        className="p-2 border border-gray-300 rounded-lg w-56"
                        value={customInputData}
                        onChange={(e) => setCustomInputData(e.target.value)}
                        onKeyDown={(e) => {
                            if (e.key === 'Enter') handleLoadCustomArray();
                        }}
                        disabled={isAnimating}
                    />
                    <button
                        className="px-4 py-2 bg-blue-500 text-white font-semibold rounded-lg hover:bg-blue-600 transition-colors shadow-md disabled:bg-gray-400"
                        onClick={handleLoadCustomArray}
                        disabled={isAnimating}
                    >
                        Load Custom Array
                    </button>
                </div>
            </div>


            {/* Error and Status Display */}
            {loadingError && (
                 <div className="mb-4 p-3 bg-red-100 border border-red-400 text-red-700 rounded-lg">
                    **Error/Status:** {loadingError}
                </div>
            )}


            {/* Visualization Area */}
            <div className="flex justify-center items-end h-[400px] w-full bg-gray-100 p-4 rounded-lg shadow-inner mb-6 overflow-x-auto">
                {currentArray.map((value, index) => {
                    const isRange = index >= currentLow && index <= currentHigh;

                    return (
                        <div key={index} style={{ position: 'relative' }}>
                            <ArrayBar
                                value={value}
                                height={(value / maxArrayValue) * maxBarHeight}
                                width={arrayBarWidthPx}
                                isHighlighted={highlightIndices.includes(index)}
                                isPivot={pivotIndex === index}
                                isSorted={sortedIndices.includes(index)}
                                // Search Specific Props
                                isSearchMid={selectedAlgorithm === 'binarysearch' && currentMid === index}
                                isSearchRange={selectedAlgorithm === 'binarysearch' && isRange}
                                isFound={isFound && (currentMid === index || currentLinearIndex === index)}
                                isLinearCheck={selectedAlgorithm === 'linearsearch' && currentLinearIndex === index}
                            />
                            {/* Low/High markers for Binary Search */}
                            {selectedAlgorithm === 'binarysearch' && (
                                <>
                                    {currentLow === index && 
                                        <span className="absolute bottom-[-15px] left-1/2 transform -translate-x-1/2 text-xs font-bold text-red-700">L</span>}
                                    {currentHigh === index && 
                                        <span className="absolute bottom-[-15px] left-1/2 transform -translate-x-1/2 text-xs font-bold text-red-700">H</span>}
                                </>
                            )}
                        </div>
                    );
                })}
            </div>

            {/* Animation Controls and Status */}
             <div className="grid grid-cols-2 gap-6">

                {/* Status and Animation Info */}
                <div className="p-4 bg-blue-50 rounded-lg border border-blue-200">
                    <h3 className="text-xl font-bold text-blue-700 mb-2">Status</h3>
                    <p className="text-gray-700 font-medium mb-2">
                        Step {currentStepIndex} of {steps?.length ? steps.length - 1 : 0} 
                        ({Math.round((currentStepIndex / ((steps?.length - 1) || 1)) * 100)}% Complete)
                    </p>
                    <p className="text-lg text-gray-900">
                        **Action:** {currentStep?.action || "Waiting for algorithm execution."}
                    </p>
                    {isSearchAlgorithm && (
                         <p className="text-sm text-gray-600 mt-1">
