                            onClick={() => setCurrentStepIndex(0)}
                            disabled={isAnimating || currentStepIndex === 0}
                        >
                            Restart
                        </button>
                        <button
                            className="px-3 py-2 bg-purple-500 text-white font-semibold rounded-lg hover:bg-purple-600 transition-colors shadow-md disabled:bg-gray-400 flex items-center"
                            onClick={() => setIsAnimating(prev => !prev)}
                            disabled={!steps || steps.length === 0 || isSortingComplete}
                        >
                            {isAnimating ? 'Pause' : 'Play'}
                        </button>
                        <button
                            className="px-3 py-2 bg-gray-500 text-white font-semibold rounded-lg hover:bg-gray-600 transition-colors shadow-md disabled:bg-gray-400 flex items-center"
                            onClick={() => setCurrentStepIndex(prev => Math.max(0, prev - 1))}
                            disabled={isAnimating || currentStepIndex === 0}
                        >
                            Prev
                        </button>
                        <button
                            className="px-3 py-2 bg-gray-500 text-white font-semibold rounded-lg hover:bg-gray-600 transition-colors shadow-md disabled:bg-gray-400 flex items-center"
                            onClick={() => setCurrentStepIndex(prev => Math.min(steps.length - 1, prev + 1))}
                            disabled={isAnimating || isSortingComplete}
                        >
                            Next
                        </button>
                         <button
                            className="px-3 py-2 bg-red-500 text-white font-semibold rounded-lg hover:bg-red-600 transition-colors shadow-md disabled:bg-gray-400 flex items-center"
                            onClick={() => setCurrentStepIndex(steps.length - 1)}
                            disabled={isAnimating || isSortingComplete}
