                            Skip
                        </button>
                    </div>
                </div>
            </div>

            {/* Pseudocode Area */}
            <div className="mt-6 p-4 bg-gray-50 rounded-lg border border-gray-200">
                <h3 className="text-xl font-bold text-gray-800 mb-2">Pseudocode: {selectedAlgorithm.toUpperCase()}</h3>
                <pre className="whitespace-pre-wrap text-sm text-gray-700 bg-white p-3 rounded border overflow-auto max-h-60">
                    {currentPseudocode ? currentPseudocode.join('\n') : 'Select an algorithm to view pseudocode.'}
                </pre>
            </div>

        </div>
    );
  };

  // Main App Return
  return (
    <div className="p-8 bg-gray-100 min-h-screen">
        <div className="flex justify-center space-x-4 mb-8">
            <button
                className={`px-6 py-2 rounded-lg font-bold transition-all ${currentView === 'sort' ? 'bg-blue-600 text-white shadow-lg' : 'bg-white text-gray-700 hover:bg-gray-200'}`}
