            </button>
            <button
                className={`px-6 py-2 rounded-lg font-bold transition-all ${currentView === 'bst' ? 'bg-blue-600 text-white shadow-lg' : 'bg-white text-gray-700 hover:bg-gray-200'}`}
                onClick={() => setCurrentView('bst')}
            >
                BST Visualizer (Dummy)
            </button>
        </div>

        <div className="max-w-7xl mx-auto">
            {currentView === 'sort' ? <ArrayVisualizer /> : <BSTVisualizer />}
        </div>

    </div>
  );
}

