// frontend/src/components/ArrayBar.js

import React from 'react';
import './ArrayBar.css';

// Must accept and use the 'height' prop for styling
const ArrayBar = ({ value, height, width, isHighlighted }) => { 
    return (
        <div 
            className={`array-bar ${isHighlighted ? 'highlighted' : ''}`} 
            style={{ 
                // CRITICAL FIX: Use the calculated height for visual scaling
                height: `${height}px`, 
                width: `${width}px`, 
                // Optional: keep the transform for positioning the value label
                transform: `translateY(${-height}px)`,
            }}>
            {/* Optional: display value */}
            <div className="bar-value">{value}</div> 
        </div>
    );
};

export default ArrayBar;
